    { id: 1, name: 'Laptop', price: 1200, category: 'Electronics', inStock: true },
    { id: 2, name: 'Phone', price: 800, category: 'Electronics', inStock: false },
    { id: 3, name: 'Book', price: 20, category: 'Books', inStock: true }
  ])

  const filters = ref({
    category: '',
    minPrice: 0,
    maxPrice: Infinity,
    inStockOnly: false
  })

  // ‚úÖ –ü—Ä–æ—Å—Ç–æ–π Getter
  const allProducts = computed(() => products.value)

  // ‚úÖ Getter —Å —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏–µ–π
  const availableProducts = computed(() =>
    products.value.filter(p => p.inStock)
  )

  // ‚úÖ Getter —Å —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–æ–π
  const productsByPrice = computed(() =>
    [...products.value].sort((a, b) => a.price - b.price)
  )

  // ‚úÖ Getter —Å –º–Ω–æ–∂–µ—Å—Ç–≤–µ–Ω–Ω—ã–º–∏ —É—Å–ª–æ–≤–∏—è–º–∏
  const filteredProducts = computed(() => {
    return products.value.filter(product => {
      const matchesCategory = !filters.value.category ||
        product.category === filters.value.category

      const matchesPrice = product.price >= filters.value.minPrice &&
        product.price <= filters.value.maxPrice

      const matchesStock = !filters.value.inStockOnly || product.inStock

      return matchesCategory && matchesPrice && matchesStock
    })
  })

  // ‚úÖ Getter —Å –ø–æ–¥—Å—á–µ—Ç–æ–º
  const productCount = computed(() => products.value.length)
  const availableCount = computed(() => availableProducts.value.length)

  // ‚úÖ Getter —Å –≥—Ä—É–ø–ø–∏—Ä–æ–≤–∫–æ–π
  const productsByCategory = computed(() => {
    const grouped: Record<string, Product[]> = {}

    products.value.forEach(product => {
      if (!grouped[product.category]) {
        grouped[product.category] = []
      }
      grouped[product.category].push(product)
    })

    return grouped
  })

  // ‚úÖ Getter —Å –ø–∞—Ä–∞–º–µ—Ç—Ä–∞–º–∏ (–∑–∞–º—ã–∫–∞–Ω–∏–µ)
  const getProductById = computed(() => {
    return (id: number) => products.value.find(p => p.id === id)
  })

  // ‚úÖ Getter —Å –ø–∞—Ä–∞–º–µ—Ç—Ä–∞–º–∏ (–º–Ω–æ–∂–µ—Å—Ç–≤–µ–Ω–Ω—ã–µ)
  const getProductsByCategory = computed(() => {
    return (category: string) =>
      products.value.filter(p => p.category === category)
  })

  // ‚úÖ Getter –¥–ª—è —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫–∏
  const stats = computed(() => ({
    total: products.value.length,
    available: availableProducts.value.length,
    averagePrice: products.value.reduce((sum, p) => sum + p.price, 0) /
      products.value.length || 0,
    categories: new Set(products.value.map(p => p.category)).size
  }))

  return {
    products,
    filters,
    // Getters
    allProducts,
    availableProducts,
    productsByPrice,
    filteredProducts,
    productCount,
    availableCount,
    productsByCategory,
    getProductById,
    getProductsByCategory,
    stats
  }
})
```

