// stores/cart.ts
import { defineStore } from 'pinia'
import { ref, computed } from 'vue'
import { useProductsStore } from './products'
import { useUserStore } from './user'

export const useCartStore = defineStore('cart', () => {
  const items = ref<{ productId: number; quantity: number }[]>([])

  // ‚úÖ –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ –¥—Ä—É–≥–æ–≥–æ store
  const productsStore = useProductsStore()
  const userStore = useUserStore()

  // Getter —Å –¥–∞–Ω–Ω—ã–º–∏ –∏–∑ –¥—Ä—É–≥–æ–≥–æ store
  const cartItems = computed(() => {
    return items.value.map(item => {
      const product = productsStore.getProductById(item.productId)
      return {
        ...product,
        quantity: item.quantity,
        subtotal: product ? product.price * item.quantity : 0
      }
    }).filter(item => item.id !== undefined)
  })

  // Getter —Å –º–Ω–æ–∂–µ—Å—Ç–≤–µ–Ω–Ω—ã–º–∏ stores
  const total = computed(() => {
    const subtotal = cartItems.value.reduce((sum, item) => sum + item.subtotal, 0)

    // –ò—Å–ø–æ–ª—å–∑—É–µ–º —Å–∫–∏–¥–∫—É –∏–∑ userStore
    const discount = userStore.isPremium ? 0.1 : 0

    return subtotal * (1 - discount)
  })

  const itemCount = computed(() =>
    items.value.reduce((sum, item) => sum + item.quantity, 0)
  )

  return {
    items,
    cartItems,
    total,
    itemCount
  }
})
```

