// stores/notifications.ts
import { defineStore } from 'pinia'
import { ref, computed, watch } from 'vue'
import { useAuthStore } from './auth'

interface Notification {
  id: number
  message: string
  type: 'info' | 'warning' | 'error'
  read: boolean
}

export const useNotificationsStore = defineStore('notifications', () => {
  const notifications = ref<Notification[]>([])

  // ‚úÖ –ö–æ–º–ø–æ–∑–∏—Ü–∏—è - –∏—Å–ø–æ–ª—å–∑—É–µ–º auth store
  const authStore = useAuthStore()

  // Getter –∑–∞–≤–∏—Å–∏—Ç –æ—Ç –¥—Ä—É–≥–æ–≥–æ store
  const unreadCount = computed(() =>
    notifications.value.filter(n => !n.read).length
  )

  // Action –∏—Å–ø–æ–ª—å–∑—É–µ—Ç –¥–∞–Ω–Ω—ã–µ –∏–∑ –¥—Ä—É–≥–æ–≥–æ store
  const fetchNotifications = async () => {
    // ‚úÖ –ü—Ä–æ–≤–µ—Ä—è–µ–º –∞—É—Ç–µ–Ω—Ç–∏—Ñ–∏–∫–∞—Ü–∏—é
    if (!authStore.isAuthenticated) {
      console.warn('User not authenticated')
      return
    }

    const response = await fetch('/api/notifications', {
      headers: {
        'Authorization': `Bearer ${authStore.token}`
      }
    })

    notifications.value = await response.json()
  }

  // ‚úÖ Watch –∑–∞ –∏–∑–º–µ–Ω–µ–Ω–∏—è–º–∏ –≤ –¥—Ä—É–≥–æ–º store
  watch(
    () => authStore.isAuthenticated,
    (isAuth) => {
      if (isAuth) {
        fetchNotifications()
      } else {
        // –û—á–∏—â–∞–µ–º –ø—Ä–∏ logout
        notifications.value = []
      }
    }
  )

  const markAsRead = (id: number) => {
    const notification = notifications.value.find(n => n.id === id)
    if (notification) {
      notification.read = true
    }
  }

  return {
    notifications,
    unreadCount,
    fetchNotifications,
    markAsRead
  }
})
