```typescript
// stores/cart.ts
import { defineStore } from 'pinia'
import { ref, computed } from 'vue'
import { useAuthStore } from './auth'
import { useProductsStore } from './products'
import { useNotificationsStore } from './notifications'

interface CartItem {
  productId: number
  quantity: number
}

export const useCartStore = defineStore('cart', () => {
  const items = ref<CartItem[]>([])

  // ‚úÖ –ö–æ–º–ø–æ–∑–∏—Ü–∏—è –Ω–µ—Å–∫–æ–ª—å–∫–∏—Ö stores
  const authStore = useAuthStore()
  const productsStore = useProductsStore()
  const notificationsStore = useNotificationsStore()

  // Getter –∏—Å–ø–æ–ª—å–∑—É–µ—Ç –¥–∞–Ω–Ω—ã–µ –∏–∑ productsStore
  const cartItems = computed(() => {
    return items.value.map(item => {
      const product = productsStore.getProductById(item.productId)
      return {
        ...product,
        quantity: item.quantity,
        subtotal: product ? product.price * item.quantity : 0
      }
    })
  })

  const total = computed(() =>
    cartItems.value.reduce((sum, item) => sum + item.subtotal, 0)
  )

  // Action –∏—Å–ø–æ–ª—å–∑—É–µ—Ç –Ω–µ—Å–∫–æ–ª—å–∫–æ stores
  const checkout = async () => {
    // ‚úÖ –ü—Ä–æ–≤–µ—Ä—è–µ–º –∞—É—Ç–µ–Ω—Ç–∏—Ñ–∏–∫–∞—Ü–∏—é
    if (!authStore.isAuthenticated) {
      throw new Error('Please login to checkout')
    }

    // ‚úÖ –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞–ª–∏—á–∏–µ —Ç–æ–≤–∞—Ä–æ–≤
    for (const item of cartItems.value) {
      const product = productsStore.getProductById(item.productId)
      if (!product?.inStock) {
        throw new Error(`Product ${product?.name} is out of stock`)
      }
    }

    // –û—Ç–ø—Ä–∞–≤–ª—è–µ–º –∑–∞–∫–∞–∑
    const response = await fetch('/api/orders', {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${authStore.token}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        userId: authStore.user!.id,
        items: items.value,
        total: total.value
      })
    })

    const order = await response.json()

    // ‚úÖ –û–±–Ω–æ–≤–ª—è–µ–º stocks –≤ productsStore
    for (const item of items.value) {
      productsStore.decrementStock(item.productId, item.quantity)
    }

    // ‚úÖ –î–æ–±–∞–≤–ª—è–µ–º —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ
    notificationsStore.addNotification({
      message: `Order #${order.id} placed successfully!`,
      type: 'success'
    })

    // –û—á–∏—â–∞–µ–º –∫–æ—Ä–∑–∏–Ω—É
    items.value = []

    return order
  }

  return {
    items,
    cartItems,
    total,
    checkout
  }
})
```

