// stores/base.ts
import { ref } from 'vue'

// ‚úÖ Composable –¥–ª—è –ø–µ—Ä–µ–∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è –ª–æ–≥–∏–∫–∏
export function useBaseStore() {
  const loading = ref(false)
  const error = ref<string | null>(null)

  const handleAsync = async <T>(
    action: () => Promise<T>
  ): Promise<T | null> => {
    loading.value = true
    error.value = null

    try {
      const result = await action()
      return result
    } catch (e: any) {
      error.value = e.message
      return null
    } finally {
      loading.value = false
    }
  }

  return {
    loading,
    error,
    handleAsync
  }
}
