```typescript
import { defineStore } from 'pinia'
import { ref } from 'vue'

const store = defineStore('test', () => {
  const count = ref(0)
  const user = ref({ name: 'John' })

  return { count, user }
})()

store.$subscribe((mutation, state) => {
  // mutation.type –º–æ–∂–µ—Ç –±—ã—Ç—å:

  // 1. 'direct' - –ø—Ä—è–º–æ–µ –∏–∑–º–µ–Ω–µ–Ω–∏–µ
  if (mutation.type === 'direct') {
    // –í—ã–∑–≤–∞–Ω–æ: store.count++
    console.log('Direct mutation')
  }

  // 2. 'patch object' - —á–µ—Ä–µ–∑ $patch —Å –æ–±—ä–µ–∫—Ç–æ–º
  if (mutation.type === 'patch object') {
    // –í—ã–∑–≤–∞–Ω–æ: store.$patch({ count: 10 })
    console.log('Patch object mutation')
  }

  // 3. 'patch function' - —á–µ—Ä–µ–∑ $patch —Å —Ñ—É–Ω–∫—Ü–∏–µ–π
  if (mutation.type === 'patch function') {
    // –í—ã–∑–≤–∞–Ω–æ: store.$patch((state) => { state.count++ })
    console.log('Patch function mutation')
  }

  // mutation.events —Å–æ–¥–µ—Ä–∂–∏—Ç –¥–µ—Ç–∞–ª–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–π
  console.log('Changed properties:', mutation.events)
})

// –ü—Ä–∏–º–µ—Ä—ã:
store.count++                           // type: 'direct'
store.$patch({ count: 10 })             // type: 'patch object'
store.$patch((state) => state.count++)  // type: 'patch function'
```

**–†–µ–∞–ª—å–Ω—ã–π –ø—Ä–∏–º–µ—Ä: –ê–≤—Ç–æ—Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ –≤ localStorage:**

```typescript
// composables/usePersistence.ts
import type { Store } from 'pinia'
import { watch } from 'vue'

export function setupPersistence(
  store: Store,
  key: string,
  options: {
    paths?: string[]  // –ö–∞–∫–∏–µ –ø–æ–ª—è —Å–æ—Ö—Ä–∞–Ω—è—Ç—å
    storage?: Storage // localStorage –∏–ª–∏ sessionStorage
  } = {}
) {
  const storage = options.storage || localStorage

  // –ó–∞–≥—Ä—É–∂–∞–µ–º –∏–∑ storage
  const savedState = storage.getItem(key)
  if (savedState) {
    try {
      const parsed = JSON.parse(savedState)
      store.$patch(parsed)
    } catch (e) {
      console.error('Failed to load state:', e)
    }
  }

  // ‚úÖ –ü–æ–¥–ø–∏—Å—ã–≤–∞–µ–º—Å—è –Ω–∞ –∏–∑–º–µ–Ω–µ–Ω–∏—è
  store.$subscribe(
    (mutation, state) => {
      // –ï—Å–ª–∏ —É–∫–∞–∑–∞–Ω—ã –∫–æ–Ω–∫—Ä–µ—Ç–Ω—ã–µ paths, —Å–æ—Ö—Ä–∞–Ω—è–µ–º —Ç–æ–ª—å–∫–æ –∏—Ö
      const dataToSave = options.paths
        ? options.paths.reduce((acc, path) => {
            acc[path] = state[path]
            return acc
          }, {} as Record<string, any>)
        : state

      storage.setItem(key, JSON.stringify(dataToSave))
    },
    { detached: true }
  )
}
```

