// plugins/actionLogger.ts
import type { PiniaPluginContext } from 'pinia'

export function actionLoggerPlugin({ store }: PiniaPluginContext) {
  // ‚úÖ –õ–æ–≥–∏—Ä–æ–≤–∞–Ω–∏–µ –≤—Å–µ—Ö actions
  store.$onAction(({
