**–†–µ–∞–ª—å–Ω—ã–π –ø–ª–∞–≥–∏–Ω: Persistence (localStorage):**

```typescript
// plugins/persistence.ts
import type { PiniaPluginContext } from 'pinia'

interface PersistenceOptions {
  enabled?: boolean
  key?: string
  paths?: string[]
  storage?: Storage
  serializer?: {
    serialize: (value: any) => string
    deserialize: (value: string) => any
  }
}

export function createPersistedState() {
  return function persistedStatePlugin({ store, options }: PiniaPluginContext) {
    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –µ—Å—Ç—å –ª–∏ –æ–ø—Ü–∏—è persist
    const persistOptions = (options as any).persist as PersistenceOptions | undefined

    if (!persistOptions?.enabled) {
      return
    }

    const {
      key = `pinia-${store.$id}`,
      paths,
      storage = localStorage,
      serializer = {
        serialize: JSON.stringify,
        deserialize: JSON.parse
      }
    } = persistOptions

    // ‚úÖ –ó–∞–≥—Ä—É–∂–∞–µ–º —Å–æ—Ö—Ä–∞–Ω–µ–Ω–Ω–æ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ
    const loadState = () => {
      try {
        const saved = storage.getItem(key)
        if (saved) {
          const state = serializer.deserialize(saved)

          // –ï—Å–ª–∏ —É–∫–∞–∑–∞–Ω—ã paths, –∑–∞–≥—Ä—É–∂–∞–µ–º —Ç–æ–ª—å–∫–æ –∏—Ö
          if (paths) {
            const partialState = paths.reduce((acc, path) => {
              if (path in state) {
                acc[path] = state[path]
              }
              return acc
            }, {} as Record<string, any>)

            store.$patch(partialState)
          } else {
            store.$patch(state)
          }
        }
      } catch (error) {
        console.error(`Failed to load state for "${store.$id}":`, error)
      }
    }

    // ‚úÖ –°–æ—Ö—Ä–∞–Ω—è–µ–º –∏–∑–º–µ–Ω–µ–Ω–∏—è
    const saveState = () => {
      try {
        const state = store.$state

        // –ï—Å–ª–∏ —É–∫–∞–∑–∞–Ω—ã paths, —Å–æ—Ö—Ä–∞–Ω—è–µ–º —Ç–æ–ª—å–∫–æ –∏—Ö
        const dataToSave = paths
          ? paths.reduce((acc, path) => {
              if (path in state) {
                acc[path] = state[path]
              }
              return acc
            }, {} as Record<string, any>)
          : state

        storage.setItem(key, serializer.serialize(dataToSave))
      } catch (error) {
        console.error(`Failed to save state for "${store.$id}":`, error)
      }
    }

    // –ó–∞–≥—Ä—É–∂–∞–µ–º –ø—Ä–∏ –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏–∏
    loadState()

    // –ü–æ–¥–ø–∏—Å—ã–≤–∞–µ–º—Å—è –Ω–∞ –∏–∑–º–µ–Ω–µ–Ω–∏—è
    store.$subscribe(() => {
      saveState()
    }, { detached: true })

    // ‚úÖ –î–æ–±–∞–≤–ª—è–µ–º –º–µ—Ç–æ–¥ –¥–ª—è –æ—á–∏—Å—Ç–∫–∏
    return {
      $clearPersistedState() {
        storage.removeItem(key)
      }
    }
  }
}
```

