```typescript
// plugins/devtools.ts
import type { PiniaPluginContext } from 'pinia'

export function devtoolsPlugin({ store }: PiniaPluginContext) {
  // ‚úÖ –î–æ–±–∞–≤–ª—è–µ–º –∫–∞—Å—Ç–æ–º–Ω—ã–µ –ø–æ–ª—è –≤ devtools
  store._customProperties = store._customProperties || new Set()

  // –î–æ–±–∞–≤–ª—è–µ–º timestamp –¥–ª—è –∫–∞–∂–¥–æ–≥–æ store
  store._createdAt = new Date()
  store._customProperties.add('_createdAt')

  // –°—á–µ—Ç—á–∏–∫ –≤—ã–∑–æ–≤–æ–≤ actions
  const actionCalls = reactive({})

  store.$onAction(({ name, after }) => {
    if (!actionCalls[name]) {
      actionCalls[name] = 0
    }

    after(() => {
      actionCalls[name]++
    })
  })

  return {
    _actionCalls: actionCalls,
    _createdAt: store._createdAt
  }
}
```

