// plugins/validation.ts
import type { PiniaPluginContext } from 'pinia'

interface ValidationRule {
  validate: (value: any) => boolean
  message: string
}

export function validationPlugin({ store }: PiniaPluginContext) {
  const validations = (store.$options as any).validations

  if (!validations) {
    return
  }

  const errors = ref<Record<string, string[]>>({})

  // ‚úÖ –í–∞–ª–∏–¥–∞—Ü–∏—è –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ state
  store.$subscribe((mutation, state) => {
    errors.value = {}

    for (const [key, rules] of Object.entries(validations) as [string, ValidationRule[]][]) {
      const value = state[key]
      const fieldErrors: string[] = []

      for (const rule of rules) {
        if (!rule.validate(value)) {
          fieldErrors.push(rule.message)
        }
      }

      if (fieldErrors.length > 0) {
        errors.value[key] = fieldErrors
      }
    }
  })

  return {
    $errors: errors,
    $isValid: computed(() => Object.keys(errors.value).length === 0)
  }
}
```

