```typescript
// stores/userForm.ts
import { defineStore } from 'pinia'
import { ref, computed } from 'vue'

interface UserForm {
  name: string
  email: string
  bio: string
  notifications: {
    email: boolean
    push: boolean
  }
}

export const useUserFormStore = defineStore('userForm', () => {
  // –ù–∞—á–∞–ª—å–Ω–æ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ
  const initialState: UserForm = {
    name: '',
    email: '',
    bio: '',
    notifications: {
      email: true,
      push: false
    }
  }

  const form = ref<UserForm>(JSON.parse(JSON.stringify(initialState)))

  // ‚úÖ Dirty check - –±—ã–ª–∏ –ª–∏ –∏–∑–º–µ–Ω–µ–Ω–∏—è
  const isDirty = computed(() => {
    return JSON.stringify(form.value) !== JSON.stringify(initialState)
  })

  // ‚úÖ –ü–æ–ª—É—á–∏—Ç—å –∏–∑–º–µ–Ω–µ–Ω–Ω—ã–µ –ø–æ–ª—è
  const changedFields = computed(() => {
    const changed: string[] = []

    for (const key in form.value) {
      if (JSON.stringify(form.value[key]) !== JSON.stringify(initialState[key])) {
        changed.push(key)
      }
    }

    return changed
  })

  // ‚úÖ Reset
  const $reset = () => {
    form.value = JSON.parse(JSON.stringify(initialState))
  }

  // ‚úÖ Load –¥–∞–Ω–Ω—ã–µ (–Ω–æ–≤–æ–µ –Ω–∞—á–∞–ª—å–Ω–æ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ)
  const loadUser = (user: UserForm) => {
    // –û–±–Ω–æ–≤–ª—è–µ–º –∏ –Ω–∞—á–∞–ª—å–Ω–æ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ, –∏ —Ç–µ–∫—É—â–µ–µ
    Object.assign(initialState, user)
    form.value = JSON.parse(JSON.stringify(user))
  }

  // ‚úÖ Patch form
  const updateForm = (updates: Partial<UserForm>) => {
    Object.assign(form.value, updates)
  }

  return {
    form,
    isDirty,
    changedFields,
    $reset,
    loadUser,
    updateForm
  }
})
```

