**Vuex store (–¥–æ –º–∏–≥—Ä–∞—Ü–∏–∏):**

```typescript
// store/modules/products.ts (Vuex)
import { Module } from 'vuex'

interface Product {
  id: number
  name: string
  price: number
}

interface ProductsState {
  products: Product[]
  loading: boolean
  error: string | null
}

const productsModule: Module<ProductsState, any> = {
  namespaced: true,

  state: () => ({
    products: [],
    loading: false,
    error: null
  }),

  // ‚ùå Mutations (–±—É–¥—É—Ç —É–¥–∞–ª–µ–Ω—ã)
  mutations: {
    SET_PRODUCTS(state, products: Product[]) {
      state.products = products
    },

    SET_LOADING(state, loading: boolean) {
      state.loading = loading
    },

    SET_ERROR(state, error: string | null) {
      state.error = error
    },

    ADD_PRODUCT(state, product: Product) {
      state.products.push(product)
    },

    UPDATE_PRODUCT(state, updatedProduct: Product) {
      const index = state.products.findIndex(p => p.id === updatedProduct.id)
      if (index !== -1) {
        state.products[index] = updatedProduct
      }
    },

    DELETE_PRODUCT(state, id: number) {
      state.products = state.products.filter(p => p.id !== id)
    }
  },

  actions: {
    async fetchProducts({ commit }) {
      commit('SET_LOADING', true)
      commit('SET_ERROR', null)

      try {
        const response = await fetch('/api/products')
        const products = await response.json()
        commit('SET_PRODUCTS', products)
      } catch (error: any) {
        commit('SET_ERROR', error.message)
      } finally {
        commit('SET_LOADING', false)
      }
    },

    async createProduct({ commit }, product: Omit<Product, 'id'>) {
      const response = await fetch('/api/products', {
        method: 'POST',
        body: JSON.stringify(product)
      })

      const newProduct = await response.json()
      commit('ADD_PRODUCT', newProduct)
      return newProduct
    }
  },

  getters: {
    productCount(state) {
      return state.products.length
    },

    expensiveProducts(state) {
      return state.products.filter(p => p.price > 100)
    },

    getProductById: (state) => (id: number) => {
      return state.products.find(p => p.id === id)
    }
  }
}

export default productsModule
```

