**Pinia store (–ø–æ—Å–ª–µ –º–∏–≥—Ä–∞—Ü–∏–∏):**

```typescript
// stores/products.ts (Pinia)
import { defineStore } from 'pinia'
import { ref, computed } from 'vue'

interface Product {
  id: number
  name: string
  price: number
}

export const useProductsStore = defineStore('products', () => {
  // ‚úÖ State
  const products = ref<Product[]>([])
  const loading = ref(false)
  const error = ref<string | null>(null)

  // ‚úÖ Getters ‚Üí computed
  const productCount = computed(() => products.value.length)

  const expensiveProducts = computed(() =>
    products.value.filter(p => p.price > 100)
  )

  const getProductById = computed(() =>
    (id: number) => products.value.find(p => p.id === id)
  )

  // ‚úÖ Actions (mutations –≤—Å—Ç—Ä–æ–µ–Ω—ã)
  const fetchProducts = async () => {
    loading.value = true
    error.value = null

    try {
      const response = await fetch('/api/products')
      products.value = await response.json()
    } catch (e: any) {
      error.value = e.message
    } finally {
      loading.value = false
    }
  }

  const createProduct = async (product: Omit<Product, 'id'>) => {
    const response = await fetch('/api/products', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(product)
    })

    const newProduct = await response.json()

    // ‚úÖ –ü—Ä—è–º–æ–µ –∏–∑–º–µ–Ω–µ–Ω–∏–µ state (–Ω–µ –Ω—É–∂–Ω—ã mutations)
    products.value.push(newProduct)

    return newProduct
  }

  const updateProduct = async (id: number, updates: Partial<Product>) => {
    const response = await fetch(`/api/products/${id}`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updates)
    })

    const updatedProduct = await response.json()

    // ‚úÖ –ü—Ä—è–º–æ–µ –∏–∑–º–µ–Ω–µ–Ω–∏–µ
    const index = products.value.findIndex(p => p.id === id)
    if (index !== -1) {
      products.value[index] = updatedProduct
    }

    return updatedProduct
  }

  const deleteProduct = async (id: number) => {
    await fetch(`/api/products/${id}`, { method: 'DELETE' })

    // ‚úÖ –ü—Ä—è–º–æ–µ –∏–∑–º–µ–Ω–µ–Ω–∏–µ
    products.value = products.value.filter(p => p.id !== id)
  }

  return {
    // State
    products,
    loading,
    error,
    // Getters
    productCount,
    expensiveProducts,
    getProductById,
    // Actions
    fetchProducts,
    createProduct,
    updateProduct,
    deleteProduct
  }
})
```

