// ‚úÖ –†–ï–®–ï–ù–ò–ï 2: Plugin –¥–ª—è persistence subscriptions
import type { PiniaPluginContext } from 'pinia'

export function hmrSubscriptionsPlugin({ store }: PiniaPluginContext) {
  const subscriptions: Array<() => void> = []

  // –°–æ—Ö—Ä–∞–Ω—è–µ–º subscriptions
  const originalSubscribe = store.$subscribe.bind(store)

  store.$subscribe = (callback, options) => {
    const unsubscribe = originalSubscribe(callback, options)

    subscriptions.push(() => {
      originalSubscribe(callback, options)
    })

    return unsubscribe
  }

  // –í–æ—Å—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –ø—Ä–∏ HMR
  if (import.meta.hot) {
    import.meta.hot.dispose(() => {
      subscriptions.forEach(resubscribe => resubscribe())
    })
  }
}
