<script setup lang="ts">
import { ref, computed } from 'vue'
import { useVirtualizer } from '@tanstack/vue-virtual'

interface Product {
  id: number
  name: string
  price: number
  category: string
  inStock: boolean
}

const products = ref<Product[]>(
  Array.from({ length: 50000 }, (_, i) => ({
    id: i + 1,
    name: `Product ${i + 1}`,
    price: Math.floor(Math.random() * 1000) + 10,
    category: ['Electronics', 'Clothing', 'Food', 'Books'][i % 4],
    inStock: Math.random() > 0.3
  }))
)

const parentRef = ref<HTMLElement | null>(null)

// –ù–∞—Å—Ç—Ä–æ–π–∫–∞ –≤–∏—Ä—Ç—É–∞–ª–∏–∑–∞—Ç–æ—Ä–∞
const virtualizer = useVirtualizer(
  computed(() => ({
    count: products.value.length,
    getScrollElement: () => parentRef.value,
    estimateSize: () => 70, // –æ—Ü–µ–Ω–æ—á–Ω–∞—è –≤—ã—Å–æ—Ç–∞ —ç–ª–µ–º–µ–Ω—Ç–∞
    overscan: 5, // –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —ç–ª–µ–º–µ–Ω—Ç–æ–≤ –¥–ª—è –ø—Ä–µ–¥–∑–∞–≥—Ä—É–∑–∫–∏
    // –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ –æ–ø—Ü–∏–∏:
    // measureElement: (el) => el.getBoundingClientRect().height, // —Ç–æ—á–Ω–æ–µ –∏–∑–º–µ—Ä–µ–Ω–∏–µ
    // scrollMargin: 0,
    // gap: 0,
    // scrollPaddingStart: 0,
    // scrollPaddingEnd: 0,
  }))
)

const virtualItems = computed(() => virtualizer.value.getVirtualItems())
const totalSize = computed(() => virtualizer.value.getTotalSize())

// –ú–µ—Ç—Ä–∏–∫–∏ –ø—Ä–æ–∏–∑–≤–æ–¥–∏—Ç–µ–ª—å–Ω–æ—Å—Ç–∏
const visibleRange = computed(() => {
  const items = virtualItems.value
  if (items.length === 0) return 'N/A'
  return `${items[0].index + 1}-${items[items.length - 1].index + 1} of ${products.value.length}`
})
</script>

