**Grid layout (–≤–∏—Ä—Ç—É–∞–ª—å–Ω–∞—è —Ç–∞–±–ª–∏—Ü–∞):**

```typescript
<script setup lang="ts">
import { ref, computed } from 'vue'
import { useVirtualizer } from '@tanstack/vue-virtual'

interface Cell {
  row: number
  col: number
  value: string
}

// –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –±–æ–ª—å—à–æ–π —Ç–∞–±–ª–∏—Ü—ã 1000x100
const ROWS = 1000
const COLS = 100

const data = ref<Cell[][]>(
  Array.from({ length: ROWS }, (_, row) =>
    Array.from({ length: COLS }, (_, col) => ({
      row,
      col,
      value: `R${row}C${col}`
    }))
  )
)

const parentRef = ref<HTMLElement | null>(null)

// –í–µ—Ä—Ç–∏–∫–∞–ª—å–Ω—ã–π –≤–∏—Ä—Ç—É–∞–ª–∏–∑–∞—Ç–æ—Ä –¥–ª—è —Å—Ç—Ä–æ–∫
const rowVirtualizer = useVirtualizer(
  computed(() => ({
    count: ROWS,
    getScrollElement: () => parentRef.value,
    estimateSize: () => 50,
    overscan: 3,
  }))
)

// –ì–æ—Ä–∏–∑–æ–Ω—Ç–∞–ª—å–Ω—ã–π –≤–∏—Ä—Ç—É–∞–ª–∏–∑–∞—Ç–æ—Ä –¥–ª—è –∫–æ–ª–æ–Ω–æ–∫
const columnVirtualizer = useVirtualizer(
  computed(() => ({
    horizontal: true,
    count: COLS,
    getScrollElement: () => parentRef.value,
    estimateSize: () => 150,
    overscan: 3,
  }))
)

const virtualRows = computed(() => rowVirtualizer.value.getVirtualItems())
const virtualColumns = computed(() => columnVirtualizer.value.getVirtualItems())
const totalHeight = computed(() => rowVirtualizer.value.getTotalSize())
const totalWidth = computed(() => columnVirtualizer.value.getTotalSize())

const visibleCells = computed(() => {
  return virtualRows.value.length * virtualColumns.value.length
})

const totalCells = ROWS * COLS
const memoryOptimization = computed(() =>
  ((1 - visibleCells.value / totalCells) * 100).toFixed(2)
)
</script>

<template>
  <div class="grid-container">
    <div class="header">
      <h2>Virtual Grid ({{ ROWS }} √ó {{ COLS }} = {{ totalCells.toLocaleString() }} cells)</h2>
      <p class="metrics">
        üî• Rendering only {{ visibleCells }} cells ({{ memoryOptimization }}% optimization)
      </p>
    </div>

    <!-- –°–∫—Ä–æ–ª–ª–∏—Ä—É–µ–º—ã–π –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä -->
    <div
      ref="parentRef"
      class="grid-scroller"
      style="overflow: auto;"
    >
      <!-- Grid —Å –ø–æ–ª–Ω—ã–º —Ä–∞–∑–º–µ—Ä–æ–º -->
      <div
        :style="{
          position: 'relative',
          height: `${totalHeight}px`,
          width: `${totalWidth}px`,
        }"
      >
        <!-- –í–∏—Ä—Ç—É–∞–ª—å–Ω—ã–µ —è—á–µ–π–∫–∏ -->
        <div
          v-for="virtualRow in virtualRows"
          :key="virtualRow.key"
          :style="{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            transform: `translateY(${virtualRow.start}px)`,
