// composables/useLazyComponent.ts
import { ref, shallowRef, type Component } from 'vue'

interface UseLazyComponentOptions {
  preload?: boolean
  onError?: (error: Error) => void
}

export function useLazyComponent(
  loader: () => Promise<any>,
  options: UseLazyComponentOptions = {}
) {
  const component = shallowRef<Component | null>(null)
  const isLoading = ref(false)
  const error = ref<Error | null>(null)

  const load = async () => {
    if (component.value) return component.value

    isLoading.value = true
    error.value = null

    try {
      const loaded = await loader()
      component.value = loaded.default || loaded
      return component.value
    } catch (e) {
      error.value = e as Error
      options.onError?.(e as Error)
      throw e
    } finally {
      isLoading.value = false
    }
  }

  // –ü—Ä–µ–¥–∑–∞–≥—Ä—É–∑–∫–∞ –ø—Ä–∏ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ—Å—Ç–∏
  if (options.preload) {
    load()
  }

  return {
    component,
    isLoading,
    error,
    load
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
