    }),

    // Gzip/Brotli compression
    compression({
      algorithm: 'gzip',
      ext: '.gz'
    }),
    compression({
      algorithm: 'brotliCompress',
      ext: '.br'
    })
  ],

  build: {
    // –ú–∏–Ω–∏—Ñ–∏–∫–∞—Ü–∏—è
    minify: 'esbuild', // –∏–ª–∏ 'terser' –¥–ª—è –ª—É—á—à–µ–π –æ–ø—Ç–∏–º–∏–∑–∞—Ü–∏–∏

    // Terser –æ–ø—Ü–∏–∏ (–µ—Å–ª–∏ –∏—Å–ø–æ–ª—å–∑—É–µ—Ç—Å—è)
    // minify: 'terser',
    // terserOptions: {
    //   compress: {
    //     drop_console: true,
    //     drop_debugger: true,
    //     pure_funcs: ['console.log', 'console.info']
    //   }
    // },

    // Target modern browsers
    target: 'es2015',

    // CSS code splitting
    cssCodeSplit: true,

    // Sourcemaps –¥–ª—è production debugging
    sourcemap: false, // –∏–ª–∏ 'hidden' –¥–ª—è debugging

    rollupOptions: {
      output: {
        // –û–ø—Ç–∏–º–∞–ª—å–Ω–æ–µ —Ä–∞–∑–±–∏–µ–Ω–∏–µ chunks
        manualChunks(id) {
          // Vendor chunks
          if (id.includes('node_modules')) {
            if (id.includes('vue') || id.includes('pinia') || id.includes('vue-router')) {
              return 'vue-vendor'
            }

            // UI –±–∏–±–ª–∏–æ—Ç–µ–∫–∏
            if (id.includes('element-plus')) {
              return 'ui-vendor'
            }

            // –¢—è–∂–µ–ª—ã–µ –±–∏–±–ª–∏–æ—Ç–µ–∫–∏ –æ—Ç–¥–µ–ª—å–Ω–æ
            if (id.includes('chart.js')) return 'charts'
            if (id.includes('monaco-editor')) return 'monaco'
            if (id.includes('@codemirror')) return 'codemirror'

            // –û—Å—Ç–∞–ª—å–Ω–æ–µ –≤ –æ–±—â–∏–π vendor
            return 'vendor'
          }
        },

        // –ù–∞–∑–≤–∞–Ω–∏—è —Ñ–∞–π–ª–æ–≤ —Å —Ö–µ—à–∞–º–∏
        chunkFileNames: 'js/[name].[hash].js',
        entryFileNames: 'js/[name].[hash].js',
        assetFileNames: (assetInfo) => {
          const info = assetInfo.name.split('.')
          const ext = info[info.length - 1]

          if (/\.(png|jpe?g|svg|gif|webp|avif)$/.test(assetInfo.name)) {
            return `images/[name].[hash].${ext}`
          }
          if (/\.css$/.test(assetInfo.name)) {
            return 'css/[name].[hash].css'
          }
          if (/\.(woff2?|eot|ttf|otf)$/.test(assetInfo.name)) {
            return `fonts/[name].[hash].${ext}`
          }

          return `assets/[name].[hash].${ext}`
        }
      }
    },

    // –†–∞–∑–º–µ—Ä warning
    chunkSizeWarningLimit: 500
  },

  // –û–ø—Ç–∏–º–∏–∑–∞—Ü–∏—è –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–µ–π
  optimizeDeps: {
    include: ['vue', 'vue-router', 'pinia'],
    exclude: ['@vueuse/core'] // –µ—Å–ª–∏ –µ—Å—Ç—å –ø—Ä–æ–±–ª–µ–º—ã
  }
})
