```typescript
// composables/useKeepAlive.ts
import { ref, computed, onActivated, onDeactivated } from 'vue'

interface KeepAliveState {
  scrollPosition: number
  formData: Record<string, any>
  timestamp: number
}

export function useKeepAliveState<T extends Record<string, any>>(
  initialData: T,
  options: {
    saveScroll?: boolean
    autoRefresh?: boolean
    refreshInterval?: number
  } = {}
) {
  const {
    saveScroll = true,
    autoRefresh = false,
    refreshInterval = 60000 // 1 –º–∏–Ω—É—Ç–∞
  } = options

  const state = ref<KeepAliveState>({
    scrollPosition: 0,
    formData: { ...initialData },
    timestamp: Date.now()
  })

  const isStale = computed(() => {
    return Date.now() - state.value.timestamp > refreshInterval
  })

  onActivated(() => {
    console.log('Component activated')

    // –í–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∏–µ scroll
    if (saveScroll) {
      setTimeout(() => {
        window.scrollTo(0, state.value.scrollPosition)
      }, 0)
    }

    // –ê–≤—Ç–æ-–æ–±–Ω–æ–≤–ª–µ–Ω–∏–µ –ø—Ä–∏ —É—Å—Ç–∞—Ä–µ–≤—à–∏—Ö –¥–∞–Ω–Ω—ã—Ö
    if (autoRefresh && isStale.value) {
      console.log('Data is stale, refreshing...')
      // Trigger refresh
    }
  })

  onDeactivated(() => {
    console.log('Component deactivated')

    // –°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ scroll
    if (saveScroll) {
      state.value.scrollPosition = window.scrollY
    }

    // –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ timestamp
    state.value.timestamp = Date.now()
  })

  const updateFormData = (data: Partial<T>) => {
    state.value.formData = { ...state.value.formData, ...data }
  }

  const resetState = () => {
    state.value = {
      scrollPosition: 0,
      formData: { ...initialData },
      timestamp: Date.now()
    }
  }

  return {
    state,
    isStale,
    updateFormData,
    resetState
  }
}

