// ============================================
// 1. ref vs shallowRef
// ============================================

interface DeepObject {
  level1: {
    level2: {
      level3: {
        value: number
      }
    }
  }
}

// Deep ref - –≤—Å–µ —É—Ä–æ–≤–Ω–∏ —Ä–µ–∞–∫—Ç–∏–≤–Ω—ã
const deepData = ref<DeepObject>({
  level1: {
    level2: {
      level3: {
        value: 0
      }
    }
  }
})

// ‚úÖ –†–ê–ë–û–¢–ê–ï–¢: –≥–ª—É–±–æ–∫–æ–µ –∏–∑–º–µ–Ω–µ–Ω–∏–µ —Ç—Ä–∏–≥–≥–µ—Ä–∏—Ç update
const updateDeep = () => {
  deepData.value.level1.level2.level3.value++
  // Vue –æ—Ç—Å–ª–µ–¥–∏—Ç —ç—Ç–æ –∏–∑–º–µ–Ω–µ–Ω–∏–µ –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏
}

// Shallow ref - —Ç–æ–ª—å–∫–æ .value —Ä–µ–∞–∫—Ç–∏–≤–Ω–æ
const shallowData = shallowRef<DeepObject>({
  level1: {
    level2: {
      level3: {
        value: 0
      }
    }
  }
})

// ‚ùå –ù–ï –†–ê–ë–û–¢–ê–ï–¢: –≥–ª—É–±–æ–∫–æ–µ –∏–∑–º–µ–Ω–µ–Ω–∏–µ –ù–ï —Ç—Ä–∏–≥–≥–µ—Ä–∏—Ç update
const updateShallowWrong = () => {
  shallowData.value.level1.level2.level3.value++
  // Vue –ù–ï –æ—Ç—Å–ª–µ–¥–∏—Ç —ç—Ç–æ –∏–∑–º–µ–Ω–µ–Ω–∏–µ!
}

// ‚úÖ –†–ê–ë–û–¢–ê–ï–¢: –∑–∞–º–µ–Ω–∞ –≤—Å–µ–≥–æ .value
const updateShallowCorrect = () => {
  shallowData.value = {
    level1: {
      level2: {
        level3: {
          value: shallowData.value.level1.level2.level3.value + 1
        }
      }
    }
  }
  // –ò–ª–∏ –∏—Å–ø–æ–ª—å–∑—É–µ–º triggerRef –¥–ª—è manual update
}

// ‚úÖ –†–ê–ë–û–¢–ê–ï–¢: manual trigger
const updateShallowWithTrigger = () => {
  shallowData.value.level1.level2.level3.value++
  triggerRef(shallowData) // –ü—Ä–∏–Ω—É–¥–∏—Ç–µ–ª—å–Ω–æ —Ç—Ä–∏–≥–≥–µ—Ä–∏–º update
}

