**Real-world use cases - –∫–æ–≥–¥–∞ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å shallow:**

```typescript
<script setup lang="ts">
import { shallowRef, shallowReactive, triggerRef, readonly } from 'vue'

// ============================================
// Use Case 1: –ë–æ–ª—å—à–∏–µ —Ç–∞–±–ª–∏—Ü—ã –¥–∞–Ω–Ω—ã—Ö
// ============================================

interface TableRow {
  id: number
  [key: string]: any
}

interface TableState {
  rows: TableRow[]
  selectedRows: Set<number>
  sortColumn: string | null
  sortDirection: 'asc' | 'desc'
}

// ‚úÖ –ò—Å–ø–æ–ª—å–∑—É–µ–º shallowReactive –¥–ª—è —Ç–∞–±–ª–∏—Ü—ã
// –ù–∞–º –Ω–µ –Ω—É–∂–Ω–∞ –≥–ª—É–±–æ–∫–∞—è —Ä–µ–∞–∫—Ç–∏–≤–Ω–æ—Å—Ç—å –¥–ª—è –∫–∞–∂–¥–æ–π —è—á–µ–π–∫–∏
const tableState = shallowReactive<TableState>({
  rows: [],
  selectedRows: new Set(),
  sortColumn: null,
  sortDirection: 'asc'
})

// –ó–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö
const loadTableData = async () => {
  const response = await fetch('/api/data?limit=10000')
  const data = await response.json()

  // ‚úÖ –ó–∞–º–µ–Ω–∞ –≤—Å–µ–≥–æ –º–∞—Å—Å–∏–≤–∞ rows —Ç—Ä–∏–≥–≥–µ—Ä–∏—Ç update
  tableState.rows = data
}

// –°–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞
const sortBy = (column: string) => {
  const direction =
    tableState.sortColumn === column && tableState.sortDirection === 'asc'
      ? 'desc'
      : 'asc'

  // ‚úÖ –°–æ–∑–¥–∞–µ–º –Ω–æ–≤—ã–π –æ—Ç—Å–æ—Ä—Ç–∏—Ä–æ–≤–∞–Ω–Ω—ã–π –º–∞—Å—Å–∏–≤ (immutable)
  tableState.rows = [...tableState.rows].sort((a, b) => {
    const aVal = a[column]
    const bVal = b[column]
    return direction === 'asc'
      ? aVal > bVal ? 1 : -1
      : aVal < bVal ? 1 : -1
  })

  tableState.sortColumn = column
  tableState.sortDirection = direction
}

// ============================================
// Use Case 2: Canvas/WebGL –¥–∞–Ω–Ω—ã–µ
// ============================================

interface CanvasState {
  width: number
  height: number
  pixels: Uint8ClampedArray // –¢–∏–ø–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–π –º–∞—Å—Å–∏–≤
  isDirty: boolean
}

// ‚úÖ shallowRef –¥–ª—è canvas –¥–∞–Ω–Ω—ã—Ö
// –¢–∏–ø–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–µ –º–∞—Å—Å–∏–≤—ã –æ–≥—Ä–æ–º–Ω—ã–µ, deep reactivity –Ω–µ –Ω—É–∂–Ω–∞
const canvasState = shallowRef<CanvasState>({
  width: 1920,
  height: 1080,
  pixels: new Uint8ClampedArray(1920 * 1080 * 4),
  isDirty: false
})

// –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ –ø–∏–∫—Å–µ–ª—è
const setPixel = (x: number, y: number, r: number, g: number, b: number, a: number) => {
  const index = (y * canvasState.value.width + x) * 4
  const pixels = canvasState.value.pixels

  pixels[index] = r
  pixels[index + 1] = g
  pixels[index + 2] = b
  pixels[index + 3] = a

  canvasState.value.isDirty = true

  // ‚úÖ Manual trigger –ø–æ—Å–ª–µ batch updates
  triggerRef(canvasState)
}

// Batch update –¥–ª—è performance
const drawRectangle = (x: number, y: number, width: number, height: number, color: [number, number, number, number]) => {
  // –ú–Ω–æ–≥–æ –æ–ø–µ—Ä–∞—Ü–∏–π —Å pixels
  for (let row = y; row < y + height; row++) {
    for (let col = x; col < x + width; col++) {
      const index = (row * canvasState.value.width + col) * 4
      canvasState.value.pixels[index] = color[0]
      canvasState.value.pixels[index + 1] = color[1]
      canvasState.value.pixels[index + 2] = color[2]
      canvasState.value.pixels[index + 3] = color[3]
    }
  }

  // ‚úÖ –û–¥–∏–Ω trigger –ø–æ—Å–ª–µ –≤—Å–µ—Ö –∏–∑–º–µ–Ω–µ–Ω–∏–π
  canvasState.value.isDirty = true
  triggerRef(canvasState)
}

// ============================================
// Use Case 3: Store state —Å immutable updates
// ============================================

interface Product {
  id: string
  name: string
  price: number
  inventory: number
}

interface StoreState {
  products: Map<string, Product>
  cart: Map<string, number> // productId -> quantity
  total: number
}

// ‚úÖ shallowReactive –¥–ª—è store
const store = shallowReactive<StoreState>({
  products: new Map(),
  cart: new Map(),
  total: 0
})

// Immutable update pattern
const addToCart = (productId: string, quantity: number = 1) => {
  // ‚úÖ –°–æ–∑–¥–∞–µ–º –Ω–æ–≤—É—é Map –¥–ª—è —Ç—Ä–∏–≥–≥–µ—Ä–∞ reactivity
  store.cart = new Map(store.cart)
  store.cart.set(
    productId,
    (store.cart.get(productId) || 0) + quantity
  )

  // –ü–µ—Ä–µ—Å—á–∏—Ç—ã–≤–∞–µ–º total
  store.total = Array.from(store.cart.entries()).reduce((sum, [id, qty]) => {
    const product = store.products.get(id)
    return sum + (product?.price || 0) * qty
  }, 0)
}

const removeFromCart = (productId: string) => {
  store.cart = new Map(store.cart)
  store.cart.delete(productId)
  // total –æ–±–Ω–æ–≤–∏—Ç—Å—è –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ —Ç.–∫. —ç—Ç–æ –ø–µ—Ä–≤—ã–π —É—Ä–æ–≤–µ–Ω—å
}

// ============================================
// Use Case 4: readonly –¥–ª—è props –∏ config
// ============================================

interface AppConfig {
