// ============================================

interface FormField {
  value: any
  error: string | null
  touched: boolean
  dirty: boolean
}

type FormState = Record<string, FormField>

// ‚úÖ shallowReactive –¥–ª—è —Ñ–æ—Ä–º—ã —Å –º–Ω–æ–∂–µ—Å—Ç–≤–æ–º –ø–æ–ª–µ–π
const formState = shallowReactive<FormState>({})

// –°–æ–∑–¥–∞–Ω–∏–µ –ø–æ–ª–µ–π
const createField = (name: string, initialValue: any = '') => {
  // ‚úÖ –ó–∞–º–µ–Ω–∞ –æ–±—ä–µ–∫—Ç–∞ –Ω–∞ –ø–µ—Ä–≤–æ–º —É—Ä–æ–≤–Ω–µ
  formState[name] = {
    value: initialValue,
    error: null,
    touched: false,
    dirty: false
  }
}

// –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ –ø–æ–ª—è
const updateField = (name: string, value: any) => {
  // ‚úÖ –°–æ–∑–¥–∞–µ–º –Ω–æ–≤—ã–π –æ–±—ä–µ–∫—Ç –ø–æ–ª—è (immutable)
  formState[name] = {
    ...formState[name],
    value,
    dirty: true
  }
}

// –í–∞–ª–∏–¥–∞—Ü–∏—è
const setFieldError = (name: string, error: string | null) => {
  formState[name] = {
    ...formState[name],
    error,
    touched: true
  }
}
