  const heights = elements.value.map(el => el.offsetHeight) // Batch READ

  elements.value.forEach((el, i) => { // Batch WRITE
    el.style.height = `${heights[i] + 10}px`
  })

  // –¢–æ–ª—å–∫–æ –æ–¥–∏–Ω layout calculation!
}

