// ============================================

const animateElement = (element: HTMLElement) => {
  let position = 0

  // ‚ùå –ü–õ–û–•–û: setTimeout –Ω–µ —Å–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∏—Ä—É–µ—Ç—Å—è —Å browser repaint
  const animateSetTimeout = () => {
    position += 1
    element.style.left = `${position}px`

    if (position < 500) {
