    }
  }

  // ‚úÖ –•–û–†–û–®–û: requestAnimationFrame —Å–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∏—Ä—É–µ—Ç—Å—è —Å browser
  const animateRAF = () => {
    position += 1
    element.style.left = `${position}px`

    if (position < 500) {
      requestAnimationFrame(animateRAF)
    }
  }

  requestAnimationFrame(animateRAF)
}

// ============================================
// Performance Observer API
// ============================================

onMounted(() => {
  // ‚úÖ –ú–æ–Ω–∏—Ç–æ—Ä–∏–Ω–≥ Long Tasks
  const observer = new PerformanceObserver((list) => {
