      // –ú–æ–∂–Ω–æ –æ—Ç–ø—Ä–∞–≤–∏—Ç—å –≤ –∞–Ω–∞–ª–∏—Ç–∏–∫—É
    }
  })

  observer.observe({ entryTypes: ['longtask'] })

  // ‚úÖ –ú–æ–Ω–∏—Ç–æ—Ä–∏–Ω–≥ Layout Shifts (CLS)
  const clsObserver = new PerformanceObserver((list) => {
