**1. Global Guards:**

```typescript
// router/index.ts
import { createRouter, createWebHistory, RouteLocationNormalized } from 'vue-router'
import { useAuthStore } from '@/stores/auth'
import { useLoadingStore } from '@/stores/loading'
import type { NavigationGuardNext } from 'vue-router'

const router = createRouter({
  history: createWebHistory(),
  routes: [...]
})

// üî• beforeEach - —Å–∞–º—ã–π –∏—Å–ø–æ–ª—å–∑—É–µ–º—ã–π guard
router.beforeEach(async (
  to: RouteLocationNormalized,
  from: RouteLocationNormalized,
  next: NavigationGuardNext
) => {
  const authStore = useAuthStore()
  const loadingStore = useLoadingStore()

  // –ü–æ–∫–∞–∑—ã–≤–∞–µ–º loading
  loadingStore.setLoading(true)

  console.log('Navigation:', from.path, '->', to.path)

  // –ü—Ä–æ–≤–µ—Ä–∫–∞ –∞—É—Ç–µ–Ω—Ç–∏—Ñ–∏–∫–∞—Ü–∏–∏
  if (to.meta.requiresAuth && !authStore.isAuthenticated) {
    // –ü–µ—Ä–µ–Ω–∞–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –Ω–∞ login —Å —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ–º intended URL
    next({
      name: 'Login',
      query: { redirect: to.fullPath }
    })
    return
  }

  // –ü—Ä–æ–≤–µ—Ä–∫–∞ —Ä–æ–ª–µ–π
  if (to.meta.roles && !authStore.hasAnyRole(to.meta.roles as string[])) {
    next({ name: 'Forbidden' })
    return
  }

  // –†–∞–∑—Ä–µ—à–∞–µ–º –Ω–∞–≤–∏–≥–∞—Ü–∏—é
  next()
})

// beforeResolve - –≤—ã–∑—ã–≤–∞–µ—Ç—Å—è –ø–æ—Å–ª–µ –≤—Å–µ—Ö guards –∏ async –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤
router.beforeResolve(async (to, from, next) => {
  // –ü—Ä–µ–¥–∑–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö
  if (to.meta.preload) {
    try {
      await to.meta.preload(to)
      next()
    } catch (error) {
      console.error('Preload failed:', error)
      next({ name: 'Error', params: { message: 'Failed to load data' } })
    }
  } else {
    next()
  }
})

// afterEach - –Ω–µ –º–æ–∂–µ—Ç –≤–ª–∏—è—Ç—å –Ω–∞ –Ω–∞–≤–∏–≥–∞—Ü–∏—é
router.afterEach((to, from, failure) => {
  const loadingStore = useLoadingStore()
  loadingStore.setLoading(false)

  // –ê–Ω–∞–ª–∏—Ç–∏–∫–∞
  if (!failure) {
    trackPageView(to.path)
  }

  // –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ title
  document.title = to.meta.title as string || 'My App'

  // Scroll to top
  if (!to.hash) {
    window.scrollTo(0, 0)
  }
})
```

