**Options API —Å –ø–æ–ª–Ω—ã–º –¥–æ—Å—Ç—É–ø–æ–º:**

```typescript
// components/EditArticle.vue - Options API
import { defineComponent } from 'vue'
import type { RouteLocationNormalized, NavigationGuardNext } from 'vue-router'

export default defineComponent({
  data() {
    return {
      article: null as Article | null,
      originalArticle: null as Article | null
    }
  },

  computed: {
    hasChanges(): boolean {
      return JSON.stringify(this.article) !== JSON.stringify(this.originalArticle)
    }
  },

  // üî• beforeRouteEnter - –≤—ã–∑—ã–≤–∞–µ—Ç—Å—è –î–û —Å–æ–∑–¥–∞–Ω–∏—è –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
  beforeRouteEnter(
    to: RouteLocationNormalized,
    from: RouteLocationNormalized,
    next: NavigationGuardNext
  ) {
    // this –ù–ï –¥–æ—Å—Ç—É–ø–µ–Ω!

    // –ó–∞–≥—Ä—É–∂–∞–µ–º –¥–∞–Ω–Ω—ã–µ –¥–æ —Å–æ–∑–¥–∞–Ω–∏—è –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
    fetch(`/api/articles/${to.params.id}`)
      .then(response => response.json())
      .then(article => {
        next(vm => {
          // –î–æ—Å—Ç—É–ø –∫ instance —á–µ—Ä–µ–∑ callback
          (vm as any).article = article
          (vm as any).originalArticle = { ...article }
        })
      })
      .catch(() => {
        next({ name: 'NotFound' })
      })
  },

  // üî• beforeRouteUpdate - –≤—ã–∑—ã–≤–∞–µ—Ç—Å—è –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ route
  async beforeRouteUpdate(
    to: RouteLocationNormalized,
    from: RouteLocationNormalized,
    next: NavigationGuardNext
  ) {
    // this –¥–æ—Å—Ç—É–ø–µ–Ω!

    if (this.hasChanges) {
      const confirmed = await this.confirmLeave()
      if (!confirmed) {
        next(false) // –û—Ç–º–µ–Ω—è–µ–º –Ω–∞–≤–∏–≥–∞—Ü–∏—é
        return
      }
    }

    // –ó–∞–≥—Ä—É–∂–∞–µ–º –Ω–æ–≤—É—é —Å—Ç–∞—Ç—å—é
    try {
      const response = await fetch(`/api/articles/${to.params.id}`)
      this.article = await response.json()
      this.originalArticle = { ...this.article }
      next()
    } catch (error) {
      next({ name: 'Error' })
    }
  },

  // üî• beforeRouteLeave - –≤—ã–∑—ã–≤–∞–µ—Ç—Å—è –ø—Ä–∏ —É—Ö–æ–¥–µ —Å route
  beforeRouteLeave(
    to: RouteLocationNormalized,
    from: RouteLocationNormalized,
    next: NavigationGuardNext
  ) {
    // this –¥–æ—Å—Ç—É–ø–µ–Ω!

    if (this.hasChanges) {
      const answer = window.confirm('–ï—Å—Ç—å –Ω–µ—Å–æ—Ö—Ä–∞–Ω–µ–Ω–Ω—ã–µ –∏–∑–º–µ–Ω–µ–Ω–∏—è. –£–π—Ç–∏?')
      next(answer)
    } else {
      next()
    }
  },

  methods: {
    async confirmLeave(): Promise<boolean> {
      return new Promise(resolve => {
        // –ú–æ–∂–Ω–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å custom dialog
        this.$dialog.confirm({
          title: '–ù–µ—Å–æ—Ö—Ä–∞–Ω–µ–Ω–Ω—ã–µ –∏–∑–º–µ–Ω–µ–Ω–∏—è',
          message: '–í—ã —É–≤–µ—Ä–µ–Ω—ã?',
          onConfirm: () => resolve(true),
          onCancel: () => resolve(false)
        })
      })
    }
  }
})
```

