// guards/auth.guard.ts - –ü–µ—Ä–µ–∏—Å–ø–æ–ª—å–∑—É–µ–º—ã–µ guards
import type { NavigationGuardNext, RouteLocationNormalized } from 'vue-router'
import { useAuthStore } from '@/stores/auth'

export const requireAuth = async (
  to: RouteLocationNormalized,
  from: RouteLocationNormalized,
  next: NavigationGuardNext
) => {
  const authStore = useAuthStore()

  if (!authStore.isAuthenticated) {
    next({
      name: 'Login',
      query: { redirect: to.fullPath }
    })
  } else {
    next()
  }
}

export const requireRole = (...roles: string[]) => {
  return async (
    to: RouteLocationNormalized,
    from: RouteLocationNormalized,
    next: NavigationGuardNext
  ) => {
    const authStore = useAuthStore()

    if (!authStore.hasAnyRole(roles)) {
      next({ name: 'Forbidden' })
    } else {
      next()
    }
  }
}

export const requirePermission = (...permissions: string[]) => {
  return async (
    to: RouteLocationNormalized,
    from: RouteLocationNormalized,
    next: NavigationGuardNext
  ) => {
    const authStore = useAuthStore()

    if (!authStore.hasAllPermissions(permissions)) {
      next({ name: 'Forbidden' })
    } else {
      next()
    }
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ:
const routes: RouteRecordRaw[] = [
  {
    path: '/admin',
    beforeEnter: [requireAuth, requireRole('admin', 'moderator')]
  },
  {
    path: '/settings',
    beforeEnter: [requireAuth, requirePermission('settings.edit')]
  }
]
