// views/User.vue - Composition API —Å Script Setup
<script setup lang="ts">
import { ref, computed, watchEffect } from 'vue'
import { useRoute, useRouter } from 'vue-router'
import type { LocationQuery } from 'vue-router'

// üî• Props –≤–∞—Ä–∏–∞–Ω—Ç (—Ä–µ–∫–æ–º–µ–Ω–¥—É–µ—Ç—Å—è –¥–ª—è –ø—Ä–æ—Å—Ç—ã—Ö —Å–ª—É—á–∞–µ–≤)
interface Props {
  id?: string // –ò–∑ route params
}

const props = defineProps<Props>()

// ‚ö° Route variant (–¥–ª—è —Å–ª–æ–∂–Ω—ã—Ö —Å–ª—É—á–∞–µ–≤)
const route = useRoute()
const router = useRouter()

// –¢–∏–ø–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–π –¥–æ—Å—Ç—É–ø –∫ params
const userId = computed(() => props.id || route.params.id as string)

// –¢–∏–ø–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–π –¥–æ—Å—Ç—É–ø –∫ query
interface UserQuery {
  tab?: string
  filter?: string
  sort?: string
}

const query = computed((): UserQuery => {
  return {
    tab: route.query.tab as string | undefined,
    filter: route.query.filter as string | undefined,
    sort: route.query.sort as string | undefined
  }
})

// –î–∞–Ω–Ω—ã–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
interface User {
  id: number
  name: string
  email: string
  role: string
}

const user = ref<User | null>(null)
const loading = ref(false)
const error = ref<string | null>(null)

// üî• –ó–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ ID
const loadUser = async (id: string) => {
  if (!id) return

  loading.value = true
  error.value = null

  try {
    const response = await fetch(`/api/users/${id}`)
    if (!response.ok) throw new Error('User not found')
    user.value = await response.json()
  } catch (e) {
    error.value = e instanceof Error ? e.message : 'Unknown error'
    user.value = null
  } finally {
    loading.value = false
  }
}

// ‚ö° –†–µ–∞–∫—Ç–∏–≤–Ω–æ—Å—Ç—å params - –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∞—è –ø–µ—Ä–µ–∑–∞–≥—Ä—É–∑–∫–∞
watchEffect(() => {
