})

// –†–∞–±–æ—Ç–∞ —Å query parameters
const activeTab = computed({
  get: () => query.value.tab || 'profile',
  set: (tab: string) => {
    router.replace({
      query: { ...route.query, tab }
    })
  }
})

// –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ query –±–µ–∑ –ø–µ—Ä–µ–∑–∞–≥—Ä—É–∑–∫–∏
const updateFilter = (filter: string) => {
  router.push({
    query: {
      ...route.query,
      filter,
      page: '1' // –°–±—Ä–æ—Å –ø–∞–≥–∏–Ω–∞—Ü–∏–∏ –ø—Ä–∏ —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏–∏
    }
  })
}

// –û—á–∏—Å—Ç–∫–∞ query –ø–∞—Ä–∞–º–µ—Ç—Ä–∞
const clearSort = () => {
  const { sort, ...restQuery } = route.query
  router.replace({ query: restQuery })
}
</script>

<template>
  <div class="user-view">
    <div v-if="loading">Loading...</div>
    <div v-else-if="error">Error: {{ error }}</div>
    <div v-else-if="user">
      <h1>{{ user.name }}</h1>
      <p>{{ user.email }}</p>

      <!-- –¢–∞–±—ã —Å query –ø–∞—Ä–∞–º–µ—Ç—Ä–∞–º–∏ -->
      <div class="tabs">
        <button
          @click="activeTab = 'profile'"
          :class="{ active: activeTab === 'profile' }"
        >
          Profile
        </button>
        <button
          @click="activeTab = 'settings'"
