// composables/useRouteParams.ts
import { computed, Ref } from 'vue'
import { useRoute } from 'vue-router'
import type { LocationQuery, LocationQueryValue } from 'vue-router'

// –¢–∏–ø–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–π —Ö–µ–ª–ø–µ—Ä –¥–ª—è params
export function useRouteParams<T extends Record<string, any>>() {
  const route = useRoute()

  return computed(() => {
    return route.params as T
  })
}

// –¢–∏–ø–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–π —Ö–µ–ª–ø–µ—Ä –¥–ª—è query
export function useRouteQuery<T extends Record<string, any>>() {
  const route = useRoute()

  return computed(() => {
    const query: Record<string, any> = {}

    for (const key in route.query) {
      const value = route.query[key]
      // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –º–∞—Å—Å–∏–≤—ã –≤ —Å—Ç—Ä–æ–∫–∏
      query[key] = Array.isArray(value) ? value[0] : value
    }

    return query as T
  })
}

// –•–µ–ª–ø–µ—Ä –¥–ª—è —Ä–∞–±–æ—Ç—ã —Å –º–∞—Å—Å–∏–≤–∞–º–∏ –≤ query
export function useQueryArray(key: string): Ref<string[]> {
  const route = useRoute()
  const router = useRouter()

  return computed({
    get: () => {
      const value = route.query[key]
      if (!value) return []
      return Array.isArray(value) ? value as string[] : [value as string]
    },
    set: (newValue: string[]) => {
      router.replace({
        query: {
          ...route.query,
          [key]: newValue.length > 0 ? newValue : undefined
        }
      })
    }
  })
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ:
