// composables/useNavigationHistory.ts
import { useRouter } from 'vue-router'
import { ref, computed } from 'vue'

export function useNavigationHistory() {
  const router = useRouter()
  const historyPosition = ref(0)

  // –û—Ç—Å–ª–µ–∂–∏–≤–∞–Ω–∏–µ –ø–æ–∑–∏—Ü–∏–∏ –≤ –∏—Å—Ç–æ—Ä–∏–∏
  router.afterEach(() => {
    historyPosition.value++
  })

  // üî• router.back() - –Ω–∞–∑–∞–¥ –Ω–∞ –æ–¥–Ω—É —Å—Ç—Ä–∞–Ω–∏—Ü—É
  const goBack = () => {
    router.back()
    // –≠–∫–≤–∏–≤–∞–ª–µ–Ω—Ç–Ω–æ: router.go(-1)
    // –≠–∫–≤–∏–≤–∞–ª–µ–Ω—Ç–Ω–æ: window.history.back()
  }

  // üî• router.forward() - –≤–ø–µ—Ä–µ–¥ –Ω–∞ –æ–¥–Ω—É —Å—Ç—Ä–∞–Ω–∏—Ü—É
  const goForward = () => {
    router.forward()
    // –≠–∫–≤–∏–≤–∞–ª–µ–Ω—Ç–Ω–æ: router.go(1)
    // –≠–∫–≤–∏–≤–∞–ª–µ–Ω—Ç–Ω–æ: window.history.forward()
  }

  // üî• router.go(n) - –Ω–∞ n –ø–æ–∑–∏—Ü–∏–π
  const goToPosition = (delta: number) => {
    router.go(delta)
    // delta > 0: –≤–ø–µ—Ä–µ–¥
    // delta < 0: –Ω–∞–∑–∞–¥
    // delta === 0: –ø–µ—Ä–µ–∑–∞–≥—Ä—É–∑–∫–∞ —Ç–µ–∫—É—â–µ–π —Å—Ç—Ä–∞–Ω–∏—Ü—ã
  }

  // –ë–µ–∑–æ–ø–∞—Å–Ω—ã–π back —Å –ø—Ä–æ–≤–µ—Ä–∫–æ–π
  const safeGoBack = () => {
    if (window.history.length > 1) {
      router.back()
    } else {
      // –ù–µ—Ç –∏—Å—Ç–æ—Ä–∏–∏ - –∏–¥–µ–º –Ω–∞ –≥–ª–∞–≤–Ω—É—é
      router.push('/')
    }
  }

  // Back —Å fallback
  const goBackOrFallback = (fallbackRoute: string = '/') => {
    const canGoBack = window.history.state?.position > 0

    if (canGoBack) {
      router.back()
    } else {
      router.push(fallbackRoute)
    }
  }

  // –ü—Ä–æ–≤–µ—Ä–∫–∞ –≤–æ–∑–º–æ–∂–Ω–æ—Å—Ç–∏ –Ω–∞–≤–∏–≥–∞—Ü–∏–∏
  const canGoBack = computed(() => window.history.length > 1)
  const canGoForward = computed(() => {
    // Forward –≤–æ–∑–º–æ–∂–µ–Ω –µ—Å–ª–∏ –º—ã –Ω–µ –≤ –∫–æ–Ω—Ü–µ –∏—Å—Ç–æ—Ä–∏–∏
    return window.history.state?.forward !== null
  })

  return {
    goBack,
    goForward,
    goToPosition,
    safeGoBack,
    goBackOrFallback,
    canGoBack,
    canGoForward
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ –≤ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–µ
