// composables/useNavigationErrors.ts
import { useRouter } from 'vue-router'
import {
  isNavigationFailure,
  NavigationFailureType,
  type NavigationFailure
} from 'vue-router'

export function useNavigationErrors() {
  const router = useRouter()

  // –¢–∏–ø—ã –æ—à–∏–±–æ–∫ –Ω–∞–≤–∏–≥–∞—Ü–∏–∏
  interface NavigationError {
    type: 'aborted' | 'cancelled' | 'duplicated' | 'unknown'
    message: string
    from: string
    to: string
  }

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –æ—à–∏–±–∫–∏
  const handleNavigationFailure = (
    failure: NavigationFailure | void,
    to: string,
    from: string
  ): NavigationError | null => {
    if (!failure) return null

    if (isNavigationFailure(failure, NavigationFailureType.aborted)) {
      return {
        type: 'aborted',
        message: 'Navigation aborted by navigation guard',
        from,
        to
      }
    }

    if (isNavigationFailure(failure, NavigationFailureType.cancelled)) {
      return {
        type: 'cancelled',
        message: 'Navigation cancelled by a new navigation',
        from,
        to
      }
    }

    if (isNavigationFailure(failure, NavigationFailureType.duplicated)) {
      return {
        type: 'duplicated',
        message: 'Navigation to current location',
        from,
        to
      }
    }

    return {
      type: 'unknown',
      message: 'Unknown navigation failure',
      from,
      to
    }
  }

  // Safe navigation —Å –æ–±—Ä–∞–±–æ—Ç–∫–æ–π –æ—à–∏–±–æ–∫
  const safeNavigate = async (to: string | object) => {
    const from = router.currentRoute.value.fullPath
    const toPath = typeof to === 'string' ? to : 'route-object'

    try {
      const result = await router.push(to as any)
      const error = handleNavigationFailure(result, toPath, from)

      if (error) {
        console.warn('Navigation issue:', error)

        // –ú–æ–∂–Ω–æ –ø–æ–∫–∞–∑–∞—Ç—å —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—é
        if (error.type === 'aborted') {
          // Navigation guard –∑–∞–±–ª–æ–∫–∏—Ä–æ–≤–∞–ª –ø–µ—Ä–µ—Ö–æ–¥
          // –ù–∞–ø—Ä–∏–º–µ—Ä, –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –Ω–µ –∞–≤—Ç–æ—Ä–∏–∑–æ–≤–∞–Ω
        }
      }

      return { success: !error, error }
    } catch (error) {
      console.error('Navigation exception:', error)
      return {
        success: false,
        error: {
          type: 'unknown' as const,
          message: error instanceof Error ? error.message : 'Unknown error',
          from,
          to: toPath
        }
      }
    }
  }

  return {
    safeNavigate,
    handleNavigationFailure
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ
