// –ù–∞–≤–∏–≥–∞—Ü–∏—è —Å –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏–µ–º
const navigateWithConfirmation = async (to: string, message: string) => {
  const confirmed = window.confirm(message)

  if (confirmed) {
    await router.push(to)
  }
}

// –ù–∞–≤–∏–≥–∞—Ü–∏—è —Å loading —Å–æ—Å—Ç–æ—è–Ω–∏–µ–º
const navigateWithLoading = async (to: string) => {
  const loadingStore = useLoadingStore()

  loadingStore.setLoading(true)
  try {
    await router.push(to)
  } finally {
    loadingStore.setLoading(false)
  }
}

// –ù–∞–≤–∏–≥–∞—Ü–∏—è —Å retry –ª–æ–≥–∏–∫–æ–π
const navigateWithRetry = async (to: string, maxRetries = 3) => {
  let retries = 0

  while (retries < maxRetries) {
    try {
      await router.push(to)
      return true
    } catch (error) {
      retries++
      if (retries >= maxRetries) {
        console.error('Navigation failed after retries:', error)
        return false
      }
      await new Promise(resolve => setTimeout(resolve, 1000))
    }
  }

  return false
}

// –ù–∞–≤–∏–≥–∞—Ü–∏—è —Å –∞–Ω–∏–º–∞—Ü–∏–µ–π
const navigateWithTransition = async (to: string, transition: string) => {
  const route = router.currentRoute.value

  // –°–æ—Ö—Ä–∞–Ω—è–µ–º transition –≤ meta –¥–ª—è –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è –≤ <router-view>
  await router.push({
    ...to,
    meta: { ...route.meta, transition }
  })
}
