// Advanced error handling —Å retry
const loadComponentWithRetry = (
  componentLoader: () => Promise<any>,
  maxRetries = 3,
  delay = 1000
) => {
  return new Promise((resolve, reject) => {
    let retries = 0

    const attempt = () => {
      componentLoader()
        .then(resolve)
        .catch((error) => {
          retries++

          if (retries >= maxRetries) {
            reject(error)
          } else {
            console.log(`Retry ${retries}/${maxRetries}...`)
            setTimeout(attempt, delay)
          }
        })
    }

    attempt()
  })
}

