**–î–∏–Ω–∞–º–∏—á–µ—Å–∫–∏–π Prefetch –Ω–∞ –æ—Å–Ω–æ–≤–µ user behavior:**

```typescript
// composables/useSmartPrefetch.ts
import { useRouter } from 'vue-router'
import { onMounted, onUnmounted } from 'vue'

export function useSmartPrefetch() {
  const router = useRouter()
  let prefetchedRoutes = new Set<string>()

  // Prefetch –ø—Ä–∏ hover –Ω–∞ —Å—Å—ã–ª–∫–µ
  const prefetchOnHover = (routeName: string) => {
    if (prefetchedRoutes.has(routeName)) return

    const route = router.getRoutes().find(r => r.name === routeName)
    if (!route) return

    // –ó–∞–ø—É—Å–∫–∞–µ–º –∑–∞–≥—Ä—É–∑–∫—É –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
    if (typeof route.component === 'function') {
      route.component()
        .then(() => {
          prefetchedRoutes.add(routeName)
          console.log(`Prefetched: ${routeName}`)
        })
        .catch(error => {
          console.error(`Failed to prefetch ${routeName}:`, error)
        })
    }
  }

  // Prefetch –Ω–∞ –æ—Å–Ω–æ–≤–µ route meta
  const prefetchRelatedRoutes = () => {
    const currentRoute = router.currentRoute.value
    const relatedRoutes = currentRoute.meta.prefetchRoutes as string[] | undefined

    if (relatedRoutes) {
      relatedRoutes.forEach(routeName => {
        setTimeout(() => prefetchOnHover(routeName), 1000)
      })
    }
  }

  // Prefetch —Å–ª–µ–¥—É—é—â–µ–≥–æ —à–∞–≥–∞ –≤ wizard
  const prefetchNextStep = (currentStep: number) => {
    const nextRouteName = `Step${currentStep + 1}`
    setTimeout(() => prefetchOnHover(nextRouteName), 500)
  }

  // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏–π prefetch –ø—Ä–∏ idle
  let idleTimeout: number

  const scheduleIdlePrefetch = (routes: string[]) => {
    const prefetchNext = () => {
      const unprefetched = routes.filter(r => !prefetchedRoutes.has(r))

      if (unprefetched.length > 0) {
        prefetchOnHover(unprefetched[0])
        idleTimeout = window.setTimeout(prefetchNext, 2000)
      }
    }

    // –ù–∞—á–∏–Ω–∞–µ–º prefetch —á–µ—Ä–µ–∑ 3 —Å–µ–∫—É–Ω–¥—ã idle
    idleTimeout = window.setTimeout(prefetchNext, 3000)
  }

  onUnmounted(() => {
    if (idleTimeout) clearTimeout(idleTimeout)
  })

  return {
    prefetchOnHover,
    prefetchRelatedRoutes,
    prefetchNextStep,
    scheduleIdlePrefetch
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ –≤ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–µ
