    meta: {
      // Routes –¥–ª—è prefetch —Å —ç—Ç–æ–π —Å—Ç—Ä–∞–Ω–∏—Ü—ã
      prefetchRoutes: ['Products', 'About'],
      // –°—Ç—Ä–∞—Ç–µ–≥–∏—è prefetch
      prefetchStrategy: 'idle' as 'idle' | 'immediate' | 'hover'
    }
  },

  {
    path: '/checkout/step1',
    name: 'CheckoutStep1',
    component: () => import('@/views/checkout/Step1.vue'),
    meta: {
      // Prefetch —Å–ª–µ–¥—É—é—â–µ–≥–æ —à–∞–≥–∞
      prefetchRoutes: ['CheckoutStep2'],
      prefetchStrategy: 'immediate'
    }
