// Navigation guard –¥–ª—è –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–æ–≥–æ prefetch
router.afterEach((to) => {
  const prefetchRoutes = to.meta.prefetchRoutes as string[] | undefined
  const strategy = to.meta.prefetchStrategy as string | undefined

  if (!prefetchRoutes) return

  if (strategy === 'immediate') {
    // Prefetch —Å—Ä–∞–∑—É
    prefetchRoutes.forEach(routeName => {
      const route = router.getRoutes().find(r => r.name === routeName)
      if (route && typeof route.component === 'function') {
        route.component()
      }
    })
  } else if (strategy === 'idle') {
    // Prefetch –≤ idle time
    if ('requestIdleCallback' in window) {
      requestIdleCallback(() => {
        prefetchRoutes.forEach(routeName => {
          const route = router.getRoutes().find(r => r.name === routeName)
          if (route && typeof route.component === 'function') {
            route.component()
          }
        })
      })
    }
  }
})
