// –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–æ–µ –¥–æ–±–∞–≤–ª–µ–Ω–∏–µ/—É–¥–∞–ª–µ–Ω–∏–µ trailing slash
router.beforeEach((to, from, next) => {
  // –£–¥–∞–ª—è–µ–º trailing slash (–∫—Ä–æ–º–µ root)
  if (to.path !== '/' && to.path.endsWith('/')) {
    next({
      path: to.path.slice(0, -1),
      query: to.query,
      hash: to.hash
    })
    return
  }

  next()
})

// –ò–ª–∏ –¥–æ–±–∞–≤–ª—è–µ–º trailing slash
router.beforeEach((to, from, next) => {
  // –î–æ–±–∞–≤–ª—è–µ–º trailing slash
  if (!to.path.endsWith('/') && !to.path.includes('.')) {
    next({
      path: to.path + '/',
      query: to.query,
      hash: to.hash
    })
    return
  }

  next()
})
