**4. Custom v-model Modifiers** üî•

```typescript
// CustomInput.vue - –ø–æ–¥–¥–µ—Ä–∂–∫–∞ –º–æ–¥–∏—Ñ–∏–∫–∞—Ç–æ—Ä–æ–≤
<script setup lang="ts">
interface Props {
  placeholder?: string
}

// –û–ø—Ä–µ–¥–µ–ª—è–µ–º –º–æ–¥–µ–ª—å —Å –º–æ–¥–∏—Ñ–∏–∫–∞—Ç–æ—Ä–∞–º–∏
const model = defineModel<string>({
  // –ú–æ–∂–Ω–æ —É—Å—Ç–∞–Ω–æ–≤–∏—Ç—å default –∑–Ω–∞—á–µ–Ω–∏–µ
  default: ''
})

// –ü–æ–ª—É—á–∞–µ–º –º–æ–¥–∏—Ñ–∏–∫–∞—Ç–æ—Ä—ã
const modifiers = defineModel.modifiers as {
  trim?: boolean
  uppercase?: boolean
  lowercase?: boolean
  capitalize?: boolean
}

// –ü—Ä–∏–º–µ–Ω—è–µ–º –º–æ–¥–∏—Ñ–∏–∫–∞—Ç–æ—Ä—ã –ø—Ä–∏ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–∏
const applyModifiers = (value: string): string => {
  let result = value

  if (modifiers.trim) {
    result = result.trim()
  }

  if (modifiers.uppercase) {
    result = result.toUpperCase()
  } else if (modifiers.lowercase) {
    result = result.toLowerCase()
  } else if (modifiers.capitalize) {
    result = result.charAt(0).toUpperCase() + result.slice(1).toLowerCase()
  }

  return result
}

const handleInput = (event: Event) => {
  const target = event.target as HTMLInputElement
  model.value = applyModifiers(target.value)
}
</script>

<template>
  <input
    :value="model"
    @input="handleInput"
    :placeholder="placeholder"
  />
</template>

<!-- –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ -->
