<CustomInput v-model.capitalize="name" />
```

**5. –°–ª–æ–∂–Ω—ã–π –ø—Ä–∏–º–µ—Ä: Date Range Picker —Å –º–Ω–æ–∂–µ—Å—Ç–≤–µ–Ω–Ω—ã–º–∏ –º–æ–¥–µ–ª—è–º–∏**

```typescript
// DateRangePicker.vue
<script setup lang="ts">
import { computed } from 'vue'

interface DateRange {
  start: Date | null
  end: Date | null
}

// –ú–æ–¥–µ–ª–∏ –¥–ª—è –Ω–∞—á–∞–ª—å–Ω–æ–π –∏ –∫–æ–Ω–µ—á–Ω–æ–π –¥–∞—Ç—ã
const startDate = defineModel<Date | null>('startDate')
const endDate = defineModel<Date | null>('endDate')

// –ú–æ–¥–µ–ª—å –¥–ª—è —Ñ–æ—Ä–º–∞—Ç–∞ –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è
const format = defineModel<string>('format', { default: 'YYYY-MM-DD' })

// –í–∞–ª–∏–¥–∞—Ü–∏—è: –∫–æ–Ω–µ—á–Ω–∞—è –¥–∞—Ç–∞ –Ω–µ –º–æ–∂–µ—Ç –±—ã—Ç—å —Ä–∞–Ω—å—à–µ –Ω–∞—á–∞–ª—å–Ω–æ–π
const isValidRange = computed(() => {
  if (!startDate.value || !endDate.value) return true
  return endDate.value >= startDate.value
})

// –§–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –¥–∞—Ç—ã
const formatDate = (date: Date | null): string => {
  if (!date) return ''

  const year = date.getFullYear()
  const month = String(date.getMonth() + 1).padStart(2, '0')
  const day = String(date.getDate()).padStart(2, '0')

  return format.value
    .replace('YYYY', String(year))
    .replace('MM', month)
    .replace('DD', day)
}

// –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ –∏–∑–º–µ–Ω–µ–Ω–∏—è –¥–∞—Ç
const handleStartChange = (event: Event) => {
  const target = event.target as HTMLInputElement
  startDate.value = target.value ? new Date(target.value) : null

  // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ –∫–æ—Ä—Ä–µ–∫—Ç–∏—Ä—É–µ–º –∫–æ–Ω–µ—á–Ω—É—é –¥–∞—Ç—É –µ—Å–ª–∏ –æ–Ω–∞ —Å—Ç–∞–ª–∞ –Ω–µ–≤–∞–ª–∏–¥–Ω–æ–π
  if (endDate.value && startDate.value && endDate.value < startDate.value) {
    endDate.value = startDate.value
  }
}

const handleEndChange = (event: Event) => {
  const target = event.target as HTMLInputElement
  const newDate = target.value ? new Date(target.value) : null

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º –≤–∞–ª–∏–¥–Ω–æ—Å—Ç—å –ø–µ—Ä–µ–¥ —É—Å—Ç–∞–Ω–æ–≤–∫–æ–π
  if (newDate && startDate.value && newDate >= startDate.value) {
    endDate.value = newDate
  } else if (!startDate.value) {
    endDate.value = newDate
  }
}

// Computed –¥–ª—è –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è –¥–∏–∞–ø–∞–∑–æ–Ω–∞
const rangeDisplay = computed(() => {
  const start = formatDate(startDate.value)
  const end = formatDate(endDate.value)

  if (!start && !end) return 'Select dates'
  if (!end) return `From ${start}`
  return `${start} - ${end}`
})
</script>

<template>
  <div class="date-range-picker">
    <div class="range-display">
      {{ rangeDisplay }}
      <span v-if="!isValidRange" class="error">Invalid range</span>
    </div>

    <div class="inputs">
      <input
        type="date"
        :value="startDate?.toISOString().split('T')[0]"
        @change="handleStartChange"
        placeholder="Start Date"
      />

      <input
        type="date"
        :value="endDate?.toISOString().split('T')[0]"
        :min="startDate?.toISOString().split('T')[0]"
        @change="handleEndChange"
        placeholder="End Date"
      />
    </div>
  </div>
</template>

<style scoped>
.date-range-picker {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}

.error {
  color: red;
  font-size: 0.875rem;
}
</style>

<!-- –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ -->
