```typescript
// types.ts
export interface ModelOptions<T> {
  default?: T
  required?: boolean
  validator?: (value: T) => boolean
}

// –¢–∏–ø–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç —Å –≤–∞–ª–∏–¥–∞—Ü–∏–µ–π
<script setup lang="ts">
interface Props {
  min?: number
  max?: number
}

const props = defineProps<Props>()

// –ú–æ–¥–µ–ª—å —Å –≤–∞–ª–∏–¥–∞—Ç–æ—Ä–æ–º
const model = defineModel<number>({
  required: true,
  validator: (value: number) => {
    if (props.min !== undefined && value < props.min) return false
    if (props.max !== undefined && value > props.max) return false
    return true
  }
})

// –¢–∏–ø–æ–±–µ–∑–æ–ø–∞—Å–Ω–æ–µ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–µ
const increment = () => {
  const newValue = (model.value || 0) + 1
  if (props.max === undefined || newValue <= props.max) {
    model.value = newValue
  }
}

const decrement = () => {
  const newValue = (model.value || 0) - 1
  if (props.min === undefined || newValue >= props.min) {
    model.value = newValue
  }
}
</script>

<template>
  <div class="number-input">
    <button @click="decrement" :disabled="min !== undefined && model <= min">
      -
    </button>
    <input
      v-model.number="model"
      type="number"
      :min="min"
      :max="max"
    />
    <button @click="increment" :disabled="max !== undefined && model >= max">
      +
    </button>
  </div>
</template>
