**7. Advanced: Computed v-model —Å getter/setter**

```typescript
// EditableList.vue - v-model —Å —Ç—Ä–∞–Ω—Å—Ñ–æ—Ä–º–∞—Ü–∏–µ–π –¥–∞–Ω–Ω—ã—Ö
<script setup lang="ts">
interface Item {
  id: string
  name: string
  selected: boolean
}

// –í–Ω–µ—à–Ω—è—è –º–æ–¥–µ–ª—å - –º–∞—Å—Å–∏–≤ ID –≤—ã–±—Ä–∞–Ω–Ω—ã—Ö —ç–ª–µ–º–µ–Ω—Ç–æ–≤
const selectedIds = defineModel<string[]>('selectedIds', { default: () => [] })

// –í–Ω—É—Ç—Ä–µ–Ω–Ω–∏–µ –¥–∞–Ω–Ω—ã–µ - –ø–æ–ª–Ω—ã–π —Å–ø–∏—Å–æ–∫ —ç–ª–µ–º–µ–Ω—Ç–æ–≤
const items = ref<Item[]>([
  { id: '1', name: 'Item 1', selected: false },
  { id: '2', name: 'Item 2', selected: false },
  { id: '3', name: 'Item 3', selected: false }
])

// Computed –¥–ª—è —Å–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∞—Ü–∏–∏ selected —Å—Ç–∞—Ç—É—Å–∞ —Å –º–æ–¥–µ–ª—å—é
const syncedItems = computed({
  get: () => {
    return items.value.map(item => ({
      ...item,
      selected: selectedIds.value.includes(item.id)
    }))
  },
  set: (newItems: Item[]) => {
    items.value = newItems
    selectedIds.value = newItems
      .filter(item => item.selected)
      .map(item => item.id)
  }
})

// –ü–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ –≤—ã–±–æ—Ä–∞ —ç–ª–µ–º–µ–Ω—Ç–∞
const toggleItem = (itemId: string) => {
  const item = items.value.find(i => i.id === itemId)
  if (!item) return

  if (selectedIds.value.includes(itemId)) {
    selectedIds.value = selectedIds.value.filter(id => id !== itemId)
  } else {
    selectedIds.value = [...selectedIds.value, itemId]
  }
}
</script>

<template>
  <div class="editable-list">
    <div
      v-for="item in syncedItems"
      :key="item.id"
      class="list-item"
      @click="toggleItem(item.id)"
    >
      <input
        type="checkbox"
        :checked="item.selected"
        @click.stop
      />
      <span>{{ item.name }}</span>
    </div>
  </div>
</template>

<!-- –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ -->
<EditableList v-model:selected-ids="selectedItemIds" />
```

