**1. Custom validators –≤ Vuelidate**

```typescript
// validators.ts - –±–∏–±–ª–∏–æ—Ç–µ–∫–∞ custom validators
import { helpers } from '@vuelidate/validators'

// –ü—Ä–æ—Å—Ç–æ–π custom validator - –ø—Ä–æ–≤–µ—Ä–∫–∞ —Å–∏–ª—ã –ø–∞—Ä–æ–ª—è
export const strongPassword = helpers.withMessage(
  'Password must contain uppercase, lowercase, number and special character',
  (value: string) => {
    if (!value) return true // optional field

    const hasUpperCase = /[A-Z]/.test(value)
    const hasLowerCase = /[a-z]/.test(value)
    const hasNumber = /[0-9]/.test(value)
    const hasSpecialChar = /[!@#$%^&*(),.?":{}|<>]/.test(value)

    return hasUpperCase && hasLowerCase && hasNumber && hasSpecialChar
  }
)

// Validator —Å –ø–∞—Ä–∞–º–µ—Ç—Ä–∞–º–∏
export const minAge = (min: number) =>
  helpers.withMessage(
    ({ $params }) => `Must be at least ${$params.min} years old`,
    (value: Date) => {
      if (!value) return true

      const age = new Date().getFullYear() - value.getFullYear()
      return age >= min
    },
    { min }
  )

// Async validator - –ø—Ä–æ–≤–µ—Ä–∫–∞ —É–Ω–∏–∫–∞–ª—å–Ω–æ—Å—Ç–∏ username
export const uniqueUsername = helpers.withAsync(
  async (value: string) => {
    if (!value || value.length < 3) return true

    try {
      const response = await fetch(`/api/check-username/${value}`)
      const data = await response.json()
      return data.isUnique
    } catch (error) {
      console.error('Username check failed:', error)
      return true // –≤ —Å–ª—É—á–∞–µ –æ—à–∏–±–∫–∏ –ø—Ä–æ–ø—É—Å–∫–∞–µ–º –≤–∞–ª–∏–¥–∞—Ü–∏—é
    }
  }
)

// Validator —Å –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç—å—é –æ—Ç –¥—Ä—É–≥–æ–≥–æ –ø–æ–ª—è
export const passwordsMatch = (passwordField: string) =>
  helpers.withMessage(
    'Passwords must match',
    function(value: string, siblings: any) {
      return value === siblings[passwordField]
    }
  )

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ –≤ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–µ
<script setup lang="ts">
import { useVuelidate } from '@vuelidate/core'
import { required, email, minLength } from '@vuelidate/validators'
import { strongPassword, uniqueUsername, minAge, passwordsMatch } from './validators'

interface RegistrationForm {
  username: string
  email: string
  password: string
  confirmPassword: string
  dateOfBirth: Date | null
}

const state = reactive<RegistrationForm>({
  username: '',
  email: '',
  password: '',
  confirmPassword: '',
  dateOfBirth: null
})

const rules = {
  username: {
    required,
    minLength: minLength(3),
    uniqueUsername // async validator
  },
  email: {
    required,
    email
  },
  password: {
    required,
    minLength: minLength(8),
    strongPassword // custom validator
  },
  confirmPassword: {
    required,
    passwordsMatch: passwordsMatch('password') // dependent validator
  },
  dateOfBirth: {
    required,
    minAge: minAge(18) // parameterized validator
  }
}

const v$ = useVuelidate(rules, state)

// –ü—Ä–æ–≤–µ—Ä–∫–∞ –∫–æ–Ω–∫—Ä–µ—Ç–Ω–æ–≥–æ –ø–æ–ª—è
const checkField = async (fieldName: keyof RegistrationForm) => {
  const field = v$.value[fieldName]
  await field.$validate()

  if (field.$error) {
    console.log(`${fieldName} validation errors:`, field.$errors)
  }
}
</script>

<template>
