    <div class="form-group">
      <input
        v-model="state.username"
        @blur="checkField('username')"
        placeholder="Username"
      />
      <!-- –ò–Ω–¥–∏–∫–∞—Ç–æ—Ä async –≤–∞–ª–∏–¥–∞—Ü–∏–∏ -->
