**3. Debouncing –¥–ª—è async validators** ‚ö°

```typescript
// composables/useDebounce.ts
export function useDebounce<T extends (...args: any[]) => any>(
  fn: T,
  delay: number = 300
) {
  let timeoutId: ReturnType<typeof setTimeout> | null = null

  const debouncedFn = (...args: Parameters<T>) => {
    return new Promise<ReturnType<T>>((resolve, reject) => {
      if (timeoutId) {
        clearTimeout(timeoutId)
      }

      timeoutId = setTimeout(async () => {
        try {
          const result = await fn(...args)
          resolve(result)
        } catch (error) {
          reject(error)
        }
      }, delay)
    })
  }

  const cancel = () => {
    if (timeoutId) {
      clearTimeout(timeoutId)
      timeoutId = null
    }
  }

  return { debouncedFn, cancel }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ debounced async validator
<script setup lang="ts">
import { ref, watch } from 'vue'
import { useDebounce } from '@/composables/useDebounce'

const username = ref('')
const isCheckingUsername = ref(false)
const usernameError = ref<string | null>(null)

// API —Ñ—É–Ω–∫—Ü–∏—è –¥–ª—è –ø—Ä–æ–≤–µ—Ä–∫–∏ username
const checkUsernameAvailability = async (value: string): Promise<boolean> => {
  const response = await fetch(`/api/check-username/${value}`)
  const data = await response.json()
  return data.isUnique
}

// Debounced –≤–µ—Ä—Å–∏—è
const { debouncedFn: debouncedCheckUsername, cancel } = useDebounce(
  checkUsernameAvailability,
  500 // 500ms –∑–∞–¥–µ—Ä–∂–∫–∞
)

// Watch —Å debounced –≤–∞–ª–∏–¥–∞—Ü–∏–µ–π
watch(username, async (newValue) => {
  if (!newValue || newValue.length < 3) {
    usernameError.value = null
    return
  }

  isCheckingUsername.value = true
  usernameError.value = null

  try {
    const isUnique = await debouncedCheckUsername(newValue)

    if (!isUnique) {
      usernameError.value = 'Username already taken'
    }
  } catch (error) {
    usernameError.value = 'Unable to check username availability'
  } finally {
    isCheckingUsername.value = false
  }
})

// –û—Ç–º–µ–Ω–∞ –ø—Ä–æ–≤–µ—Ä–∫–∏ –ø—Ä–∏ unmount
onUnmounted(() => {
  cancel()
})
</script>

<template>
  <div class="form-group">
    <input
      v-model="username"
      placeholder="Username"
      :class="{
        'is-validating': isCheckingUsername,
        'is-invalid': usernameError
      }"
    />

    <div class="validation-feedback">
      <span v-if="isCheckingUsername" class="checking">
        ‚è≥ Checking availability...
      </span>
      <span v-else-if="!usernameError && username.length >= 3" class="valid">
        ‚úÖ Username available
      </span>
      <span v-else-if="usernameError" class="error">
        ‚ùå {{ usernameError }}
      </span>
    </div>
  </div>
</template>
```

**4. Server-side validation integration**

```typescript
// composables/useServerValidation.ts
interface ServerError {
  field: string
  message: string
}

interface ServerValidationResponse {
  isValid: boolean
  errors: ServerError[]
}

export function useServerValidation() {
  const serverErrors = ref<Map<string, string>>(new Map())

  const validateOnServer = async (
    formData: Record<string, any>
  ): Promise<boolean> => {
    try {
      const response = await fetch('/api/validate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      })

      const data: ServerValidationResponse = await response.json()

      // –û—á–∏—â–∞–µ–º –ø—Ä–µ–¥—ã–¥—É—â–∏–µ –æ—à–∏–±–∫–∏
      serverErrors.value.clear()

      // –ó–∞–ø–æ–ª–Ω—è–µ–º –Ω–æ–≤—ã–º–∏ –æ—à–∏–±–∫–∞–º–∏
      data.errors.forEach(error => {
        serverErrors.value.set(error.field, error.message)
      })

      return data.isValid
    } catch (error) {
      console.error('Server validation failed:', error)
      return false
    }
  }

  const getError = (field: string): string | undefined => {
    return serverErrors.value.get(field)
  }

  const clearError = (field: string) => {
    serverErrors.value.delete(field)
  }

  const clearAllErrors = () => {
    serverErrors.value.clear()
  }

  return {
    serverErrors: readonly(serverErrors),
    validateOnServer,
    getError,
    clearError,
    clearAllErrors
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ –≤ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–µ
<script setup lang="ts">
