**1. –ë–∞–∑–æ–≤—ã–π file upload —Å –≤–∞–ª–∏–¥–∞—Ü–∏–µ–π**

```typescript
// FileUpload.vue - –±–∞–∑–æ–≤—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç –∑–∞–≥—Ä—É–∑–∫–∏ —Ñ–∞–π–ª–æ–≤
<script setup lang="ts">
import { ref, computed } from 'vue'

interface FileValidationOptions {
  maxSize: number // –≤ –±–∞–π—Ç–∞—Ö
  allowedTypes: string[] // MIME types
  maxFiles?: number
}

interface UploadedFile {
  file: File
  id: string
  preview?: string
  progress: number
  status: 'pending' | 'uploading' | 'success' | 'error'
  error?: string
}

const props = withDefaults(defineProps<{
  modelValue?: File[]
  accept?: string
  multiple?: boolean
  maxSize?: number // MB
  allowedTypes?: string[]
}>(), {
  multiple: false,
  maxSize: 5, // 5MB default
  allowedTypes: () => ['image/jpeg', 'image/png', 'image/gif', 'application/pdf']
})

const emit = defineEmits<{
  'update:modelValue': [files: File[]]
  'upload-complete': [files: UploadedFile[]]
  'upload-error': [error: string]
}>()

const fileInput = ref<HTMLInputElement | null>(null)
const uploadedFiles = ref<UploadedFile[]>([])
const isDragging = ref(false)

// –í–∞–ª–∏–¥–∞—Ü–∏—è —Ñ–∞–π–ª–∞
const validateFile = (file: File): { isValid: boolean; error?: string } => {
  const maxBytes = props.maxSize * 1024 * 1024

  // –ü—Ä–æ–≤–µ—Ä–∫–∞ —Ä–∞–∑–º–µ—Ä–∞
  if (file.size > maxBytes) {
    return {
      isValid: false,
      error: `File size exceeds ${props.maxSize}MB limit`
    }
  }

  // –ü—Ä–æ–≤–µ—Ä–∫–∞ —Ç–∏–ø–∞
  if (!props.allowedTypes.includes(file.type)) {
    return {
      isValid: false,
      error: `File type ${file.type} is not allowed`
    }
  }

  return { isValid: true }
}

// –°–æ–∑–¥–∞–Ω–∏–µ preview –¥–ª—è –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π
const createPreview = async (file: File): Promise<string | undefined> => {
  if (!file.type.startsWith('image/')) return undefined

  return new Promise((resolve) => {
    const reader = new FileReader()
    reader.onload = (e) => resolve(e.target?.result as string)
    reader.readAsDataURL(file)
  })
}

// –û–±—Ä–∞–±–æ—Ç–∫–∞ –≤—ã–±–æ—Ä–∞ —Ñ–∞–π–ª–æ–≤
const handleFileSelect = async (event: Event) => {
  const target = event.target as HTMLInputElement
  const files = Array.from(target.files || [])

  await processFiles(files)
}

// –û–±—Ä–∞–±–æ—Ç–∫–∞ drag and drop
const handleDrop = async (event: DragEvent) => {
  event.preventDefault()
  isDragging.value = false

  const files = Array.from(event.dataTransfer?.files || [])
  await processFiles(files)
}

// –û–±—â–∞—è –æ–±—Ä–∞–±–æ—Ç–∫–∞ —Ñ–∞–π–ª–æ–≤
const processFiles = async (files: File[]) => {
  // –ü—Ä–æ–≤–µ—Ä–∫–∞ –∫–æ–ª–∏—á–µ—Å—Ç–≤–∞ —Ñ–∞–π–ª–æ–≤
  if (!props.multiple && files.length > 1) {
    emit('upload-error', 'Only one file is allowed')
    return
  }

  // –í–∞–ª–∏–¥–∞—Ü–∏—è –∏ —Å–æ–∑–¥–∞–Ω–∏–µ preview –¥–ª—è –∫–∞–∂–¥–æ–≥–æ —Ñ–∞–π–ª–∞
  for (const file of files) {
    const validation = validateFile(file)

    if (!validation.isValid) {
      emit('upload-error', validation.error!)
      continue
    }

    const preview = await createPreview(file)

    const uploadedFile: UploadedFile = {
      file,
      id: `${Date.now()}-${Math.random()}`,
      preview,
      progress: 0,
      status: 'pending'
    }

    uploadedFiles.value.push(uploadedFile)
  }

  // –û–±–Ω–æ–≤–ª—è–µ–º v-model
  const allFiles = uploadedFiles.value.map(uf => uf.file)
  emit('update:modelValue', allFiles)
}

// –ó–∞–≥—Ä—É–∑–∫–∞ —Ñ–∞–π–ª–∞ –Ω–∞ —Å–µ—Ä–≤–µ—Ä
const uploadFile = async (uploadedFile: UploadedFile) => {
  const formData = new FormData()
  formData.append('file', uploadedFile.file)

  try {
    uploadedFile.status = 'uploading'

    // XMLHttpRequest –¥–ª—è –æ—Ç—Å–ª–µ–∂–∏–≤–∞–Ω–∏—è –ø—Ä–æ–≥—Ä–µ—Å—Å–∞
    await new Promise<void>((resolve, reject) => {
      const xhr = new XMLHttpRequest()

      // –û—Ç—Å–ª–µ–∂–∏–≤–∞–Ω–∏–µ –ø—Ä–æ–≥—Ä–µ—Å—Å–∞
      xhr.upload.addEventListener('progress', (e) => {
        if (e.lengthComputable) {
          uploadedFile.progress = Math.round((e.loaded / e.total) * 100)
        }
      })

      xhr.addEventListener('load', () => {
        if (xhr.status >= 200 && xhr.status < 300) {
          uploadedFile.status = 'success'
          resolve()
        } else {
          reject(new Error(`Upload failed with status ${xhr.status}`))
        }
      })

      xhr.addEventListener('error', () => {
        reject(new Error('Upload failed'))
      })

      xhr.open('POST', '/api/upload')
      xhr.send(formData)
    })

  } catch (error) {
    uploadedFile.status = 'error'
    uploadedFile.error = error instanceof Error ? error.message : 'Upload failed'
    emit('upload-error', uploadedFile.error)
  }
}

// –ó–∞–≥—Ä—É–∑–∫–∞ –≤—Å–µ—Ö —Ñ–∞–π–ª–æ–≤
const uploadAll = async () => {
  const pendingFiles = uploadedFiles.value.filter(f => f.status === 'pending')

  await Promise.all(
    pendingFiles.map(file => uploadFile(file))
  )

  emit('upload-complete', uploadedFiles.value)
}

// –£–¥–∞–ª–µ–Ω–∏–µ —Ñ–∞–π–ª–∞
const removeFile = (fileId: string) => {
  uploadedFiles.value = uploadedFiles.value.filter(f => f.id !== fileId)

  const allFiles = uploadedFiles.value.map(uf => uf.file)
  emit('update:modelValue', allFiles)
}

// Drag and drop handlers
const handleDragEnter = (event: DragEvent) => {
  event.preventDefault()
  isDragging.value = true
}

const handleDragLeave = (event: DragEvent) => {
  event.preventDefault()
  isDragging.value = false
}

const handleDragOver = (event: DragEvent) => {
  event.preventDefault()
}

// –¢—Ä–∏–≥–≥–µ—Ä –Ω–∞—Ç–∏–≤–Ω–æ–≥–æ input
const triggerFileSelect = () => {
  fileInput.value?.click()
}

// –§–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ —Ä–∞–∑–º–µ—Ä–∞ —Ñ–∞–π–ª–∞
const formatFileSize = (bytes: number): string => {
  if (bytes === 0) return '0 Bytes'

  const k = 1024
  const sizes = ['Bytes', 'KB', 'MB', 'GB']
  const i = Math.floor(Math.log(bytes) / Math.log(k))

  return Math.round(bytes / Math.pow(k, i) * 100) / 100 + ' ' + sizes[i]
}
</script>

<template>
  <div class="file-upload">
    <!-- Drag and Drop Area -->
    <div
      class="drop-zone"
      :class="{ 'is-dragging': isDragging }"
      @drop="handleDrop"
      @dragenter="handleDragEnter"
      @dragleave="handleDragLeave"
      @dragover="handleDragOver"
      @click="triggerFileSelect"
    >
      <div class="drop-zone-content">
        <svg class="upload-icon" viewBox="0 0 24 24">
          <path d="M9 16h6v-6h4l-7-7-7 7h4v6zm-4 2h14v2H5v-2z"/>
        </svg>
        <p class="drop-text">
          Drag and drop files here or <span class="link">browse</span>
        </p>
        <p class="drop-hint">
          Max {{ maxSize }}MB ‚Ä¢ {{ allowedTypes.join(', ') }}
        </p>
      </div>

      <!-- Native Input (hidden) -->
