**2. Multiple file uploads —Å chunked upload –¥–ª—è –±–æ–ª—å—à–∏—Ö —Ñ–∞–π–ª–æ–≤** üî•

```typescript
// ChunkedFileUpload.vue
<script setup lang="ts">
interface ChunkUploadOptions {
  chunkSize: number // —Ä–∞–∑–º–µ—Ä —á–∞–Ω–∫–∞ –≤ –±–∞–π—Ç–∞—Ö
  maxRetries: number
  retryDelay: number
}

const defaultOptions: ChunkUploadOptions = {
  chunkSize: 1024 * 1024, // 1MB chunks
  maxRetries: 3,
  retryDelay: 1000
}

class ChunkedUploader {
  private file: File
  private options: ChunkUploadOptions
  private uploadedChunks: Set<number> = new Set()

  constructor(file: File, options: ChunkUploadOptions = defaultOptions) {
    this.file = file
    this.options = options
  }

  async upload(
    onProgress: (progress: number) => void,
    onComplete: (fileUrl: string) => void,
    onError: (error: Error) => void
  ): Promise<void> {
    const totalChunks = Math.ceil(this.file.size / this.options.chunkSize)

    // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –∑–∞–≥—Ä—É–∑–∫–∏ –Ω–∞ —Å–µ—Ä–≤–µ—Ä–µ
    const uploadId = await this.initializeUpload()

    try {
      // –ó–∞–≥—Ä—É–∑–∫–∞ —á–∞–Ω–∫–æ–≤ –ø–∞—Ä–∞–ª–ª–µ–ª—å–Ω–æ (–º–∞–∫—Å 3 –æ–¥–Ω–æ–≤—Ä–µ–º–µ–Ω–Ω–æ)
      const chunkPromises: Promise<void>[] = []

      for (let chunkIndex = 0; chunkIndex < totalChunks; chunkIndex++) {
        const promise = this.uploadChunk(chunkIndex, uploadId, totalChunks)
          .then(() => {
            this.uploadedChunks.add(chunkIndex)
            const progress = Math.round((this.uploadedChunks.size / totalChunks) * 100)
            onProgress(progress)
          })

        chunkPromises.push(promise)

        // –û–≥—Ä–∞–Ω–∏—á–µ–Ω–∏–µ –ø–∞—Ä–∞–ª–ª–µ–ª—å–Ω—ã—Ö –∑–∞–≥—Ä—É–∑–æ–∫
        if (chunkPromises.length >= 3) {
          await Promise.race(chunkPromises)
        }
      }

      // –û–∂–∏–¥–∞–Ω–∏–µ –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è –≤—Å–µ—Ö —á–∞–Ω–∫–æ–≤
      await Promise.all(chunkPromises)

      // –§–∏–Ω–∞–ª–∏–∑–∞—Ü–∏—è –∑–∞–≥—Ä—É–∑–∫–∏
      const fileUrl = await this.finalizeUpload(uploadId)
      onComplete(fileUrl)

    } catch (error) {
      onError(error as Error)
    }
  }

  private async initializeUpload(): Promise<string> {
    const response = await fetch('/api/upload/init', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        fileName: this.file.name,
        fileSize: this.file.size,
        mimeType: this.file.type
      })
    })

    const data = await response.json()
    return data.uploadId
  }

  private async uploadChunk(
    chunkIndex: number,
    uploadId: string,
    totalChunks: number,
    retryCount: number = 0
  ): Promise<void> {
    const start = chunkIndex * this.options.chunkSize
    const end = Math.min(start + this.options.chunkSize, this.file.size)
    const chunk = this.file.slice(start, end)

    const formData = new FormData()
    formData.append('chunk', chunk)
    formData.append('chunkIndex', String(chunkIndex))
    formData.append('uploadId', uploadId)
    formData.append('totalChunks', String(totalChunks))

    try {
      const response = await fetch('/api/upload/chunk', {
        method: 'POST',
        body: formData
      })

      if (!response.ok) {
        throw new Error(`Chunk upload failed: ${response.statusText}`)
      }
    } catch (error) {
      // Retry logic
      if (retryCount < this.options.maxRetries) {
        await new Promise(resolve => setTimeout(resolve, this.options.retryDelay))
        return this.uploadChunk(chunkIndex, uploadId, totalChunks, retryCount + 1)
      }

      throw error
    }
  }

  private async finalizeUpload(uploadId: string): Promise<string> {
    const response = await fetch('/api/upload/finalize', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ uploadId })
    })

    const data = await response.json()
    return data.fileUrl
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ –≤ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–µ
const uploadLargeFile = async (file: File) => {
  const uploader = new ChunkedUploader(file)

  await uploader.upload(
    (progress) => {
      console.log(`Upload progress: ${progress}%`)
    },
    (fileUrl) => {
      console.log(`Upload complete: ${fileUrl}`)
    },
    (error) => {
      console.error('Upload failed:', error)
    }
  )
}
</script>
```

**3. Direct upload to S3/Cloud Storage**

```typescript
// S3DirectUpload.vue
<script setup lang="ts">
interface S3UploadConfig {
  presignedUrl: string
  fields: Record<string, string>
}

const getS3UploadConfig = async (file: File): Promise<S3UploadConfig> => {
  const response = await fetch('/api/s3/presigned-url', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({
      fileName: file.name,
      fileType: file.type,
      fileSize: file.size
    })
  })

  return response.json()
}

const uploadToS3 = async (
  file: File,
  onProgress: (progress: number) => void
): Promise<string> => {
  // –ü–æ–ª—É—á–∞–µ–º presigned URL –∏ fields
  const config = await getS3UploadConfig(file)

  // –°–æ–∑–¥–∞—ë–º FormData —Å –ø–æ–ª—è–º–∏ –æ—Ç S3
  const formData = new FormData()

  Object.entries(config.fields).forEach(([key, value]) => {
    formData.append(key, value)
  })

  // –§–∞–π–ª –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –ø–æ—Å–ª–µ–¥–Ω–∏–º
  formData.append('file', file)

  // –ó–∞–≥—Ä—É–∑–∫–∞ –Ω–∞–ø—Ä—è–º—É—é –≤ S3
  return new Promise((resolve, reject) => {
    const xhr = new XMLHttpRequest()

    xhr.upload.addEventListener('progress', (e) => {
      if (e.lengthComputable) {
        const progress = Math.round((e.loaded / e.total) * 100)
        onProgress(progress)
      }
    })

    xhr.addEventListener('load', () => {
      if (xhr.status >= 200 && xhr.status < 300) {
        // URL —Ñ–∞–π–ª–∞ –≤ S3
        const fileUrl = `${config.presignedUrl}/${config.fields.key}`
        resolve(fileUrl)
      } else {
        reject(new Error(`S3 upload failed: ${xhr.statusText}`))
      }
    })

    xhr.addEventListener('error', () => {
      reject(new Error('S3 upload failed'))
    })

    xhr.open('POST', config.presignedUrl)
    xhr.send(formData)
  })
}
</script>
