**1. –ë–∞–∑–æ–≤—ã–π Linear Wizard —Å Composition API** üî•

```typescript
// composables/useWizard.ts
import { ref, computed, readonly } from 'vue'

export interface WizardStep {
  id: string
  title: string
  component: Component
  isValid?: () => boolean | Promise<boolean>
  onEnter?: () => void | Promise<void>
  onLeave?: () => void | Promise<void>
}

export function useWizard(steps: WizardStep[]) {
  const currentStepIndex = ref(0)
  const completedSteps = ref<Set<number>>(new Set())
  const stepsData = ref<Map<string, any>>(new Map())

  const currentStep = computed(() => steps[currentStepIndex.value])
  const isFirstStep = computed(() => currentStepIndex.value === 0)
  const isLastStep = computed(() => currentStepIndex.value === steps.length - 1)

  const progress = computed(() => {
    return Math.round(((currentStepIndex.value + 1) / steps.length) * 100)
  })

  const canGoNext = computed(async () => {
    const step = currentStep.value
    if (!step.isValid) return true

    try {
      return await step.isValid()
    } catch {
      return false
    }
  })

  const goToStep = async (stepIndex: number) => {
    if (stepIndex < 0 || stepIndex >= steps.length) return false

    // –í—ã–∑—ã–≤–∞–µ–º onLeave –¥–ª—è —Ç–µ–∫—É—â–µ–≥–æ —à–∞–≥–∞
    const currentStepObj = steps[currentStepIndex.value]
    if (currentStepObj.onLeave) {
      await currentStepObj.onLeave()
    }

    currentStepIndex.value = stepIndex

    // –í—ã–∑—ã–≤–∞–µ–º onEnter –¥–ª—è –Ω–æ–≤–æ–≥–æ —à–∞–≥–∞
    const newStep = steps[stepIndex]
    if (newStep.onEnter) {
      await newStep.onEnter()
    }

    return true
  }

  const nextStep = async () => {
    if (isLastStep.value) return false

    // –í–∞–ª–∏–¥–∞—Ü–∏—è —Ç–µ–∫—É—â–µ–≥–æ —à–∞–≥–∞
    const isValid = await canGoNext.value
    if (!isValid) return false

    // –û—Ç–º–µ—á–∞–µ–º —à–∞–≥ –∫–∞–∫ –∑–∞–≤–µ—Ä—à—ë–Ω–Ω—ã–π
    completedSteps.value.add(currentStepIndex.value)

    return goToStep(currentStepIndex.value + 1)
  }

  const prevStep = async () => {
    if (isFirstStep.value) return false
    return goToStep(currentStepIndex.value - 1)
  }

  const setStepData = (stepId: string, data: any) => {
    stepsData.value.set(stepId, data)
  }

  const getStepData = (stepId: string) => {
    return stepsData.value.get(stepId)
  }

  const getAllData = () => {
    return Object.fromEntries(stepsData.value)
  }

  const reset = () => {
    currentStepIndex.value = 0
    completedSteps.value.clear()
    stepsData.value.clear()
  }

  return {
    currentStep: readonly(currentStep),
    currentStepIndex: readonly(currentStepIndex),
    isFirstStep: readonly(isFirstStep),
    isLastStep: readonly(isLastStep),
    progress: readonly(progress),
    completedSteps: readonly(completedSteps),
    goToStep,
    nextStep,
    prevStep,
    setStepData,
    getStepData,
    getAllData,
    reset
  }
}
```

**2. Wizard –∫–æ–º–ø–æ–Ω–µ–Ω—Ç —Å Progress Indicator**

```typescript
// Wizard.vue - –æ—Å–Ω–æ–≤–Ω–æ–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç wizard
<script setup lang="ts">
import { provide } from 'vue'
import type { WizardStep } from './useWizard'

interface Props {
  steps: WizardStep[]
  showProgress?: boolean
  allowStepNavigation?: boolean
}

const props = withDefaults(defineProps<Props>(), {
  showProgress: true,
  allowStepNavigation: false
})

const emit = defineEmits<{
  'complete': [data: Record<string, any>]
  'cancel': []
}>()

const {
  currentStep,
  currentStepIndex,
  isFirstStep,
  isLastStep,
  progress,
  completedSteps,
  nextStep,
  prevStep,
  goToStep,
  getAllData,
  setStepData,
  getStepData,
  reset
} = useWizard(props.steps)

// Provide wizard context –¥–ª—è –¥–æ—á–µ—Ä–Ω–∏—Ö –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤
provide('wizard', {
  setStepData,
  getStepData,
  nextStep,
  prevStep
})

const handleNext = async () => {
  const success = await nextStep()

  if (success && isLastStep.value) {
    // –ó–∞–≤–µ—Ä—à–µ–Ω–∏–µ wizard
    emit('complete', getAllData())
  }
}

const handlePrevious = async () => {
  await prevStep()
}

const handleStepClick = async (stepIndex: number) => {
  if (!props.allowStepNavigation) return

  // –ú–æ–∂–Ω–æ –ø–µ—Ä–µ—Ö–æ–¥–∏—Ç—å —Ç–æ–ª—å–∫–æ –Ω–∞ –∑–∞–≤–µ—Ä—à—ë–Ω–Ω—ã–µ —à–∞–≥–∏ –∏–ª–∏ —Å–ª–µ–¥—É—é—â–∏–π
  if (
    completedSteps.value.has(stepIndex) ||
    stepIndex === currentStepIndex.value + 1
  ) {
    await goToStep(stepIndex)
  }
}

const handleCancel = () => {
  reset()
  emit('cancel')
}
</script>

<template>
